// lib
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

// Component
import ViewTask from "../../task/view/ViewTask";

import GetTasksFunc from "../../task/actions/GetTasksFunc";
import GetCollectionFunc from "../../collection/actions/GetCollectionFunc"

// Service
import IsEmptyObject from "../../../../service/IsEmptyObject";

const useStyles = createUseStyles({
  content: {
    textAlign: "left",
    maxWidth: "unset",
  },
});

export default function ListTasks() {
  const classes = useStyles();

  const [data, setData] = useState({});
  const [listColl, setListColl] = useState([]);

  useEffect(async () => {
    //   get Tasks
    const tasksData = await GetTasksFunc(4);
    handleChangeData(tasksData.data);
  }, []);

  useEffect(async () => {
    //   get Collections
    const res = await GetCollectionFunc(10);
    setListColl(res.data.items)
  }, []);

  const handleChangeData = (data) => {
    console.log(data);
    setData(data);
  };

  return (
    <div className="ViewCollection">
      <div className={clsx(classes.content, "content")}>
        <ViewTask data={data} handleChangeData={handleChangeData} listColl={listColl} />
      </div>
    </div>
  );
}
