import React from "react";
import { useState, useEffect } from "react";

function getStatus(status) {
  switch (status) {
    case "all":
      status = "All Task";
      break;
    case "inProcess":
      status = "InProcess Task";
      break;
    case "complete":
      status = "Complete Task";
      break;
    default:
      status = "All Task";
      break;
  }

  return status;
}

export default function TaskBar({ count, status }) {
  const [listStatus, setListStatus] = useState([
    {name : "all", value : "All Task"},
    {name : "inProcess", value : "In Pro Task"},
    {name : "complete", value : "All Task"},
    "",
    "",
  ]);
  const [selectedOption, setSelectedOption] = useState(listStatus[0].value);

  function getComboA(selectObject) {
    var value = selectObject.value;
    console.log(value);
  }

  return (
    <div
      className="task__title"
      style={{
        fontSize: "2rem",
        textTransform: "capitalize",
        padding: "2rem",
        display: "flex",
        justifyContent: "space-between",
      }}
    >
      <div className="taskBar__details">
        {getStatus(status)} - {count}
      </div>

      <div className="taskBar__actions">
        <div className="taskBar__action">
          <select
            className="taskBar__select"
            value={selectedOption}
            onChange={(e) => setSelectedOption(e.target.value)}
          >
            {listStatus.map((status) => {
              return (
                <option key={status} value={status} className="taskBar__option">
                  {status}
                </option>
              );
            })}
          </select>
        </div>
      </div>
    </div>
  );
}
