import { useState, useEffect, memo } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";
import axios from "axios";

import TaskBar from "./TaskBar";
import Task from "./Task";

import IsEmptyObject from "../../../../service/IsEmptyObject";
import Paging from "./Paging";

const useStyles = createUseStyles({
  tasks__tab: {
    width: "80%",
    maxWidth: "100rem",
  },
  tasks: {
    display: "grid",
    gap: "1.7rem",
    gridTemplateColumns: "repeat(auto-fit, minmax(20rem, 1fr))",
  },
});

function ViewTask({ data, handleChangeData, listColl }) {
  const classes = useStyles();

  const [selectedTasks, setSelectedTasks] = useState([]);
  const handleSelectTasks = (id) => (event) => {
    setSelectedTasks([...selectedTasks, id]);

    console.log(selectedTasks);
  };

  return (
    <div className={clsx(classes.tasks__tab)}>
      <TaskBar
        count={data && data.meta ? data.meta.totalItems : 0}
        handleChangeData={handleChangeData}
        handleChangeStatus={handleChangeStatus}
      />

      <div className={clsx(classes.tasks)}>
        {data &&
          data.items &&
          data.items.map((task) => {
            return (
              <Task
                key={task.id}
                listColl={listColl}
                task={task}
                handleChangeData={handleChangeData}
                handleSelectTasks={handleSelectTasks}
              />
            );
          })}
      </div>

      {data && data.meta && data.meta.totalItems > 0 && (
        <Paging data={data} handleChangeData={handleChangeData} />
      )}
    </div>
  );
}

export default memo(ViewTask);
