// lib
import { createUseStyles } from "react-jss";
import clsx from "clsx";
import { useState, useEffect } from "react";
import axios from "axios";
import Select from "react-select";

import useTaskForm from "../services/useTaskForm";
import GetCollectionFunc from "../GetTasksFunc";

import IsEmptyObject from "../../../../../service/IsEmptyObject";

const useStyles = createUseStyles({
  EditTask: {
    top: "0",
    left: "0",
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    zIndex: "11",
    color: "#FFFFFF",
  },
  editTask__bg: {
    backgroundColor: "#000",
    opacity: "70%",
  },
  editTask__container: {
    width: "42rem",
    minWidth: "48rem",
    marginTop: "6rem",
    padding: "2rem",
    backgroundColor: "#1D1D27",
    textAlign: "left",
    borderRadius: "1.6rem",
  },
  editTask__caption: {
    fontSize: "2rem",
    textTransform: "capitalize",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  editTask__title: {
    fontSize: "1.6rem",
    textTransform: "capitalize",
    marginBottom: "1rem",
  },
  editTask__colors: {
    display: "grid",
    gap: "1.5rem",
    gridTemplateColumns: "repeat(auto-fit, 6rem)",
  },
  editTask__color: {
    width: "6rem",
    height: "5rem",
    backgroundColor: "transparent",
    cursor: "pointer",
    borderRadius: "1.2rem",
    transition: "all 0.1s ease-in-out",
    position: "relative",
  },
  editTask__buttons: {
    gap: "1rem",
    marginTop: "5rem",
  },
  editTask__group: {
    marginBottom: "2rem",
    width: "100%",
  },
  editTask__err: {
    color: "red",
    padding: "1rem",
    fontSize: "1.4rem",
  },
  editTask__cbbox: {
    color: "var(--bg-btn-pink)",
    backgroundColor: "transparent",
    fontSize: "1.4rem",
  },
});

const EditTask = ({ listColl, task, toggleFunc, displayVal }) => {
  const classes = useStyles();

  const [selectedOption, setSelectedOption] = useState([]);
  const [allCollsConvert, setAllCollsConvert] = useState([]);


  const HandleEditTask = (values) => {
    const title = values.title;

    let listSelection = selectedOption.map((item) => {
      return { id: item.value, name: item.label };
    });
    let categories = [...task.categories, ...listSelection];

    console.log(categories);
    // axios({
    //   method: "post",
    //   url: "api/categories/${collection.id}",
    //   data: { title },
    // })
    //   .then((res) => {
    //     console.log(res);
    //   })
    //   .catch((err) => {
    //     const errMsg = err.response.data.message;
    //     console.log(errMsg);
    //   });
  };

  const { handleChange, handleSubmit, values, errors } =
    useTaskForm(HandleEditTask);

  const closeTabFunc = () => (event) => {
    toggleFunc(false)();
  };

  const handleChangeOption = (optionsID, checked) => {
    console.log(optionsID);
    console.log(checked)
  };

  return (
    <div
      className={clsx(
        classes.EditTask,
        "m-auto position-fixed",
        !displayVal && "d-none"
      )}
    >
      <div
        className={clsx(classes.editTask__bg, "w-100 h-100 position-absolute")}
        onClick={closeTabFunc(false)}
      ></div>

      <div className={clsx(classes.editTask__container, "position-absolute")}>
        <form method="post" onSubmit={handleSubmit}>
          <div className="editTask__top d-flex w-100">
            <div className={clsx(classes.editTask__caption, "mr-auto")}>
              Edit Task
            </div>
            <div className="editTask__close-btn" onClick={closeTabFunc(false)}>
              <ion-icon name="close"></ion-icon>
            </div>
          </div>

          <div className={clsx(classes.editTask__group, "bar-title")}>
            <div className={clsx(classes.editTask__title)}>Title</div>
            <input
              type="text"
              placeholder="My Task"
              className="btn--outline w-100 py-3"
              style={{ fontSize: "1.6rem" }}
              name="title"
              value={values.title}
              onChange={handleChange}
            />
            {errors.title && (
              <div className={clsx(classes.editTask__err)}>*{errors.title}</div>
            )}
          </div>
          <div className={clsx(classes.editTask__group, "bar-listColl")}>
            <div className={clsx(classes.editTask__title)}>
              Current Collection
            </div>
            <div className={clsx(classes.editTask__listColls)}>
              {listColl.map((item) => {
                let checked = task.categories.find((i) => {
                  return i.id === item.id;
                });
                return (
                  <div className={clsx(classes.editTask__itemColl)}>
                    <input
                      type="checkbox"
                      className={clsx(classes.editTask__checkbox)}
                      checked={checked}
                      onChange={handleChangeOption(item.id, checked)}
                    />
                    <span className={clsx(classes.editTask__lable)}>
                      {item.name}
                    </span>
                  </div>
                );
              })}
            </div>
          </div>

          <div className={clsx(classes.editTask__buttons, "w-100 d-flex")}>
            <button
              className="editTask__button button btn--none-border btn--hover-bg-gray-light ml-auto mb-0"
              style={{ backgroundColor: "#3D3C50" }}
              type="submit"
            >
              Submit
            </button>
            <button
              type="reset"
              className="editTask__button button btn--none-border btn--hover-bg-gray mb-0"
              onClick={closeTabFunc()}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditTask;
