import React from "react";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

const useStyles = createUseStyles({
  taskBar__container: {
    textTransform: "capitalize",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  },
  taskBar__actions: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "1rem",
  },
  taskBar__action: {
    height: "100%",
  },
  taskBar__input: {
    minWidth: "5rem",
    padding: "1rem",
    width: "3rem !important",
    fontSize: "1.5rem",
  },
  taskBar__detail: {
    fontSize: "2rem",
  },
  taskBar__lable: {
    fontSize: "1.5rem",
  },
});

export default function TaskBar({
  status,
  count,
  handleChangeData,
  handleChangeStatus,
}) {
  const classes = useStyles();

  const handleRemoveTasks = (event) => {};

  return (
    <div className={clsx(classes.taskBar__container)}>
      <div className={clsx(classes.taskBar__details)}>
        <div className={clsx(classes.taskBar__detail)}>Total : {count}</div>
      </div>

      <div className={clsx(classes.taskBar__actions)}>
        <div
          className={clsx(classes.taskBar__action)}
          onClick={handleRemoveTasks}
        >
          <ion-icon name="trash"></ion-icon>
        </div>
      </div>
    </div>
  );
}
