import axios from "axios";

export default function taskAPI() {
  const createTask = async (data) => {
    let res = await axios.post("auth/tasks", {...data});
    return res.data;
  };

  const getTasks = async (limit = 8, page = 1, search = "") => {
    const res = await axios.get(
      `api/tasks?limit=${limit}&page=${page}&search=${search}`
    );
    return res.data;
  };

  const getTask = async (taskID) => {
    const res = await axios.get(`api/tasks/${taskID}`);
    return res.data;
  };

  const updateTask = async (taskID, data) => {
    const res = await axios.patch(`api/tasks/${taskID}`, data);
    return res.data;
  };

  const deleteTask = async (taskID) => {
    const res = await axios.delete(`api/users/${taskID}`);
    return res.data;
  };

  return { createTask, getTasks, getTask, updateTask, deleteTask };
}
