// lib
import React from "react";
import { createUseStyles } from "react-jss";
import { Routes, Route } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

// Component
import Home from "./components/home/Home";
import ScreenSaver from "./components/screenSaver/ScreenSaver";
import Users from "./components/users/Users";
import Header from "./components/header/Header";

import Routers from "./routers/Routers";

// Css
import "./App.css";

// =================================================================
const useStyles = createUseStyles({
  App: {
    backgroundImage: "url(" + "https://tsks.app/img/signin_balls.png" + ")",
    backgroundColor: "#181820",
    backgroundPosition: "top",
    backgroundSize: "contain",
    backgroundRepeat: "no-repeat",
  },
});

function App() {
  const classes = useStyles();
  const [user, setUser] = useState({ username: "", password: "" });
  const [error, setError] = useState("");

  // Function
  const Register = async ({ username, password, ...args }) => {
    let res = await axios({
      method: "post",
      url: "https://mvn-task-manager.work/auth/register",
      data: { username, password },
    });

    console.log("res", res);
    console.log("res.data", res.data);
  };
  const Login = async ({ username, password, ...args }) => {
    let res = await axios({
      method: "post",
      url: "https://mvn-task-manager.work/auth/login",
      data: { username, password },
    });

    if (res && res.data) {
      console.log(res.data);

      setUser({
        username: username,
        password: password,
      });
      console.log("Logged in");
    } else {
      console.log("Details not match!");
      setError("Username or password incorrect");
    }
  };
  const Logout = () => {
    setUser({
      username: "",
      password: "",
    });
    console.log(user);
  };
  // End-Function

  return (
    <div className={classes.App}>
      <Header user={user} Logout={Logout} />

      <Routes>
        {user.username !== "" ? (
          <Route exact path="" element={<Home />} />
        ) : (
          <Route
            exact
            path=""
            element={<ScreenSaver user={user} Login={Login} />}
          />
        )}
      </Routes>

      <Routers Login={Login} Register={Register} error={error} />
    </div>
  );
}
export default App;
