import axios from "axios";

export default async function userAPI() {
  const register = async (details) => {
    let res = await axios.post("auth/register", details);

    let data = res.data;
    return data;
  };
  const login = async (details) => {
    let res = await axios.post("auth/login", details);

    let data = res.data;
    return data;
  };
  const getUser = async (userID) => {
    const res = await axios.get(`api/users/${userID}`);
    return res;
  };
  const updateUser = async (userID, details) => {
    const res = await axios.patch(`api/users/${userID}`, details);
    return res;
  };
  const deleteUser = async (userID) => {
    const { data } = await axios.delete(`api/users/${userID}`);
    return data;
  };

  return {register, login, getUser,}
}
