import React from "react";
import { useState, useEffect } from "react";

function getStatus(status) {
  switch (status) {
    case "all":
      status = "All Task";
      break;
    case "inProcess":
      status = "InProcess Task";
      break;
    case "complete":
      status = "Complete Task";
      break;
    default:
      status = "All Task";
      break;
  }

  return status;
}

export default function TaskBar({ count, status }) {
  const [listStatus, setListStatus] = useState([
    "all",
    "inProcess",
    "complete",
  ]);

  return (
    <div
      className="task__title"
      style={{
        fontSize: "2rem",
        textTransform: "capitalize",
        padding: "2rem",
      }}
    >
    <div></div>
      {getStatus(status)} - {count}

      <select>
        {listStatus.map((status) => {
          return <option value={status}>{status}</option>;
        })}
      </select>
    </div>
  );
}
