// lib
import { createUseStyles } from "react-jss";
import { Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import clsx from "clsx";

// Component
import { ChromePicker } from "react-color";
import Collection from "./collection/Collection";
import axios from "axios";

const useStyles = createUseStyles({
  title: {
    fontSize: "3rem",
    fontWeight: "bold",
    margin: "2rem 0",
  },
  collections: {
    width: "100%",
    display: "grid",
    gap: "1.7rem",
    transition: "all 0.3s ease",
  },
  actions: {
    gap: "1rem",
  },
  action: {
    padding: "1.2rem 2.4rem !important",
    margin: "auto",
    borderRadius: "1.5rem",
  },
  // //////////////////////////

  AddCollection: {
    top: "0",
    left: "0",
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    zIndex: "11",
    color: "#FFFFFF",
  },
  addCollection__bg: {
    backgroundColor: "#000",
    opacity: "70%",
  },
  addCollection__container: {
    width: "42rem",
    minWidth: "48rem",
    marginTop: "6rem",
    padding: "2rem",
    backgroundColor: "#1D1D27",
    textAlign: "left",
    borderRadius: "1.6rem",
  },
  addCollection__caption: {
    fontSize: "2rem",
    textTransform: "capitalize",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  addCollection__title: {
    fontSize: "1.6rem",
    textTransform: "capitalize",
    marginBottom: "1rem",
  },
  addCollection__colors: {
    display: "grid",
    gap: "1.5rem",
    gridTemplateColumns: "repeat(auto-fit, 6rem)",
  },
  addCollection__color: {
    width: "6rem",
    height: "5rem",
    backgroundColor: "transparent",
    cursor: "pointer",
    borderRadius: "1.2rem",
    transition: "all 0.1s ease-in-out",
    position: "relative",
  },
  addCollection__buttons: {
    gap: "1rem",
    marginTop: "5rem",
  },
  addCollection__bar: {
    marginBottom: "2rem",
    width: "100%",
  },
  addCollection__chromePicker: {
    position: "absolute",
    right: "100%",
    bottom: "100%",
  },
});

const useStyles2 = createUseStyles({
  collection: {
    padding: "2.2rem 2.5rem",
    borderRadius: "2rem",
    gap: "2rem",
    cursor: "pointer",
    backgroundColor: "var(--background-color-header)",
    transition: "all ease 0.3s",
  },
  collection__tag: {
    padding: "1.3rem",
    borderRadius: "1.5rem",
  },
  collection__name: {
    width: "fit-content",
    textAlign: "left",
    fontSize: "2rem",
    textTransform: "capitalize",
    display: "-webkit-box",

    overflow: "hidden",
    textOverflow: "ellipsis",
    wordBreak: "break-word",
  },
  collection__countTasks: {
    color: "#A1A1A6",
    fontSize: "1.6rem",
    width: "fit-content",
  },
});

const HandleData = () => {};

export default function Home() {
  const classes = useStyles();

  // Var
  const [collections, setCollections] = useState([]);
  const [displayVal, setDisplayVal] = useState(false);

  useEffect(() => {
    axios
      .get(`https://mvn-task-manager.work/api/categories?limit=10&page=1`)
      .then((res) => {
        const items = res.data.items;
        console.log(items);
        setCollections(items);
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);

        // TODO : Call toast message
      });
  }, []);

  // Function
  const toggleAddCollectionBar = (value) => (event) => {
    setDisplayVal(value);
  };

  // Component
  const ListCollection = () => {
    return (
      <div className="ListCollection">
        <div
          className="content"
          style={{
            minWidth: `${collections.length > 0 ? `70rem` : `40rem`}`,
          }}
        >
          <div
            className={clsx(
              classes.topBar,
              "w-100",
              collections.length > 0 ? "d-flex" : "",
              "align-items-center",
              "justify-content-between"
            )}
          >
            <div
              className={clsx(
                classes.title,
                collections.length == 0 ? "w-100" : ""
              )}
            >
              {collections.length > 0
                ? "Collections"
                : "You have no collections."}
            </div>

            <div
              className={clsx(
                classes.actions,
                collections.length > 0 ? "d-flex" : "w-100"
              )}
            >
              <button
                className={clsx(
                  classes.action,
                  collections.length == 0 ? "d-none" : "",
                  "searchCollection button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
                )}
              >
                <ion-icon name="search-outline"></ion-icon>
              </button>

              <button
                className={clsx(
                  classes.action,
                  "addCollection button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
                )}
                onClick={toggleAddCollectionBar(true)}
              >
                <ion-icon name="add-outline"></ion-icon>
                {collections.length > 0 ? "" : "Add Your First Collection"}
              </button>
            </div>
          </div>

          <div
            className={classes.collections}
            style={{
              gridTemplateColumns: `repeat(${
                collections.length > 0 ? "2" : "1"
              },1fr)`,
            }}
          >
            {collections &&
              collections.length > 0 &&
              collections.map((collection) => {
                return (
                  <Collection key={collection.cId} collection={collection} />
                );
              })}
          </div>
        </div>

        <AddCollection />
      </div>
    );
  };

  const AddCollection = () => {
    
  };

  return (
    <div className="Home">
      <ListCollection />
    </div>
  );
}
