// lib
import { createUseStyles } from "react-jss";
import { Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import clsx from "clsx";
import axios from "axios";

import IsEmptyObject from "../../../../../service/IsEmptyObject";

const useStyles = createUseStyles({
  AddCollection: {
    top: "0",
    left: "0",
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    zIndex: "11",
    color: "#FFFFFF",
  },
  addCollection__bg: {
    backgroundColor: "#000",
    opacity: "70%",
  },
  addCollection__container: {
    width: "42rem",
    minWidth: "48rem",
    marginTop: "6rem",
    padding: "2rem",
    backgroundColor: "#1D1D27",
    textAlign: "left",
    borderRadius: "1.6rem",
  },
  addCollection__caption: {
    fontSize: "2rem",
    textTransform: "capitalize",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  addCollection__title: {
    fontSize: "1.6rem",
    textTransform: "capitalize",
    marginBottom: "1rem",
  },
  addCollection__colors: {
    display: "grid",
    gap: "1.5rem",
    gridTemplateColumns: "repeat(auto-fit, 6rem)",
  },
  addCollection__color: {
    width: "6rem",
    height: "5rem",
    backgroundColor: "transparent",
    cursor: "pointer",
    borderRadius: "1.2rem",
    transition: "all 0.1s ease-in-out",
    position: "relative",
  },
  addCollection__buttons: {
    gap: "1rem",
    marginTop: "5rem",
  },
  addCollection__group: {
    marginBottom: "2rem",
    width: "100%",
  },
  addCollection__chromePicker: {
    position: "absolute",
    right: "100%",
    bottom: "100%",
  },
  addCollection__err: {
    color: "red",
    padding: "1rem",
    fontSize: "1.4rem",
  },
});

const HandleCreateCollection = ({ name, ...args }) => {};

const useForm = () => {
  const [values, setValues] = useState({ name: "" });
  const [errors, setErrors] = useState({});

  const validateInfo = (values) => {
    let errors = {};

    const nameTemp = values.name.trim();

    if (!nameTemp) {
      errors.name = "Name must be required";
    } else if (nameTemp.length < 6) {
      errors.name = "Name to be 6 charactor or more";
    }

    return errors;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };
  const handleSubmit = (e) => {
    e.preventDefault();

    let errorsTemp = validateInfo(values);
    setErrors(errorsTemp);

    if (IsEmptyObject(errorsTemp)) {
      HandleCreateCollection(values);
    }
  };

  return { handleChange, handleSubmit, values, errors };
};

const AddCollection = ({ toggleFunc, displayVal }) => {
  const classes = useStyles();

  // Var
  const [listColors, setListColors] = useState([
    "pink",
    "yellow",
    "orange",
    "red",
    "purple",
    "blue",
  ]);
  const [collectionColor, setCollectionColor] = useState("");

  const { handleChange, handleSubmit, values, errors } = useForm();

  // Function
  const setBgColor = (color) => (event) => {
    setCollectionColor(color);
  };
  const closeTabFunc = () => (event) => {
    setCollectionColor("");
    toggleFunc(false)();
  };

  return (
    <div
      className={clsx(
        classes.AddCollection,
        "m-auto position-fixed",
        !displayVal && "d-none"
      )}
    >
      <div
        className={clsx(
          classes.addCollection__bg,
          "w-100 h-100 position-absolute"
        )}
        onClick={closeTabFunc(false)}
      ></div>

      <div
        className={clsx(classes.addCollection__container, "position-absolute")}
      >
        <form method="post" onSubmit={handleSubmit}>
          <div className="addCollection__top d-flex w-100">
            <div className={clsx(classes.addCollection__caption, "mr-auto")}>
              add collection
            </div>
            <div
              className="addCollection__close-btn"
              onClick={closeTabFunc(false)}
            >
              <ion-icon name="close"></ion-icon>
            </div>
          </div>

          <div className={clsx(classes.addCollection__group, "bar-name")}>
            <div className={clsx(classes.addCollection__title)}>name</div>
            <input
              type="text"
              placeholder="My Collection"
              className="btn--outline w-100 py-3"
              style={{ fontSize: "1.6rem" }}
              name="name"
              value={values.name}
              onChange={handleChange}
            />
            {errors.name && (
              <div className={clsx(classes.addCollection__err)}>
                *{errors.name}
              </div>
            )}
          </div>

          <div className={clsx(classes.addCollection__group, "bar-color")}>
            <div className={clsx(classes.addCollection__title)}>color</div>
            <div className={clsx(classes.addCollection__colors, "w-100")}>
              {listColors.map(function (color) {
                return (
                  <div
                    className={clsx(classes.addCollection__color)}
                    key={`${color}`}
                    style={{
                      border: `3px solid ${color}`,
                      backgroundColor:
                        collectionColor === color ? `${color}` : "transparent",
                    }}
                    onClick={setBgColor(color)}
                  ></div>
                );
              })}
            </div>
          </div>

          <div className={clsx(classes.addCollection__buttons, "w-100 d-flex")}>
            <button
              className="addCollection__button button btn--none-border btn--hover-bg-gray-light ml-auto mb-0"
              style={{ backgroundColor: "#3D3C50" }}
              type="submit"
            >
              Create
            </button>
            <button
              type="reset"
              className="addCollection__button button btn--none-border btn--hover-bg-gray mb-0"
              onClick={closeTabFunc()}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddCollection;
