// lib
import { createUseStyles } from "react-jss";
import { Routes, Route, Link, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import clsx from "clsx";
import axios from "axios";

// Compo

// Service
import NavigateLink from "../../../../service/NavigateLink";

function AddTask() {
  return (
    <div
      className="doing-tasks__bar position-absolute  align-items-center "
      style={{
        display: "none",
        width: "30rem",
        padding: "0 1.3rem",
        borderRadius: "1.3rem",
        border: "3px solid var(--background-color-header)",
      }}
    >
      <div
        className="doing-tasks__button d-flex"
        style={{
          padding: "0.3rem",
          borderRadius: "0.7rem",
        }}
      >
        <ion-icon
          name="add"
          style={{
            fontSize: "1.7rem",
            cursor: "pointer",
          }}
        ></ion-icon>
      </div>
      <div className="doing-tasks__input w-100">
        <input
          type="text"
          placeholder="Add task"
          className="w-100 input--none-border"
          style={{
            fontSize: "1.6rem",
          }}
        />
      </div>
      <div className="doing-tasks__button">
        <ion-icon
          name="close"
          style={{
            fontSize: "2rem",
            cursor: "pointer",
          }}
        ></ion-icon>
      </div>
    </div>
  );
}
function SearchTasks() {
  return (
    <div
      className="doing-tasks__bar position-absolute align-items-center"
      style={{
        display: "flex",
        right: "0",
        width: "30rem",
        paddingRight: "1.3rem",
        borderRadius: "1.3rem",
        border: "3px solid var(--background-color-header)",
      }}
    >
      <div className="doing-tasks__input w-100">
        <input
          type="text"
          placeholder="Input tasks name"
          className="w-100 input--none-border"
          style={{
            fontSize: "1.6rem",
          }}
        />
      </div>

      <div className="doing-tasks__button">
        <ion-icon
          name="search"
          style={{
            fontSize: "2rem",
            cursor: "pointer",
          }}
        ></ion-icon>
      </div>
    </div>
  );
}

export default function ViewCollection() {
  let { cId } = useParams();

  const [collection, setCollection] = useState({});

  useEffect(() => {
    if (cId) {
      axios
        .get(`api/categories/${cId}`)
        .then((res) => {
          setCollection(res.data);
          console.log(collection.name);
        })
        .catch((err) => {
          const errMsg = err.response.data.message;
          console.log(errMsg);
        });
    }
  }, []);

  const handleChange = (data) => {
    setCollection(data);
  };

  console.log("object");
  return (
    <div className="ViewCollection">
      <div
        className="content d-flex flex-column"
        style={{
          textAlign: "left",
          maxWidth: "70rem",
          minWidth: "70rem",
        }}
      >
        <div className="tasks__tab d-flex justify-content-between w-100 mt-5">
          <CollectionBar handleChange={handleChange} />
        </div>
      </div>
    </div>
  );
}
