import React from "react";
import { useState, useEffect } from "react";

export default function TaskBar({
  status,
  count,
  handleChangeData,
  handleChangeStatus,
}) {
  const [listStatus, setListStatus] = useState([
    { name: "All Task", value: "all" },
    { name: "In Process Task", value: "inProcess" },
    { name: "Complete Task", value: "complete" },
  ]);
  const [selectedOption, setSelectedOption] = useState();

  function handleChange(event) {
    const optionVal = event.target.value;
    console.log(event.target);
  }

  return (
    <div
      className="task__title"
      style={{
        fontSize: "2rem",
        textTransform: "capitalize",
        padding: "2rem",
        display: "flex",
        justifyContent: "space-between",
      }}
    >
      <div className="taskBar__details">Total : {count}</div>

      <div className="taskBar__actions">
        <div className="taskBar__action">
          <select className="taskBar__select" onChange={handleChange}>
            {listStatus.map((item) => {
              return (
                <option
                  key={item.value}
                  value={item.value}
                  className="taskBar__option"
                >
                  {item.name}
                </option>
              );
            })}
          </select>
        </div>
      </div>
    </div>
  );
}
