import React from "react";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

const useStyles = createUseStyles({
  task__bar: {
    borderRadius: "1.3rem",
    padding: "1rem",
    display: "flex",
    flexDirection: "column",
    gap: "1rem",
    width: "100%",
    position: "relative",
  },
  task__btns: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    position: "relative",
  },
  collection__actions: {
    minWidth: "max-content",
    top: "100%",
    right: "5%",
    borderRadius: "1.1rem",
    backgroundColor: "#1e1e29",
    zIndex: "2",
    border: "3px solid var(--background-color-header)",
    position: "absolute",
  },
  collection__action: {
    fontSize: "1.3rem",
    padding: "1.2rem 1.8rem",
  },
  task__title: { width: "100%", fontSize: "1.6rem", flexGrow: "1" },
});

export default function Task({ task, handleChangeData }) {
  const classes = useStyles();
  console.log(task);

  const [displayActionsBarVal, setDisplayActionsBarVal] = useState(false);

  const toggelActionsBar = (e) => {};

  return (
    <div
      className={clsx(classes.task__bar)}
      style={{
        border:
          task.status === "IN_PROGRESS"
            ? `3px solid var(--background-color-header)`
            : "3px solid red",
      }}
    >
      <div className={clsx(classes.task__btns)}>
        <ion-icon
          name="ellipsis-vertical"
          style={{
            fontSize: "2rem",
            marginLeft: "auto",
          }}
          onClick={toggelActionsBar}
        ></ion-icon>
        <div className={clsx(classes.collection__actions)}>
          <div
            className={clsx(
              classes.collection__action,
              " button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            )}
          >
            edit task
          </div>
          <div
            className={clsx(
              classes.collection__action,
              " button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            )}
          >
            Submit Task
          </div>
          <div
            className={clsx(
              classes.collection__action,
              " button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            )}
          >
            delete task
          </div>
        </div>
      </div>

      <div className={clsx(classes.task__title)}>{task.title}</div>

    
    </div>
  );
}
