// lib
import { createUseStyles } from "react-jss";
import { Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import clsx from "clsx";

// Component
import Home from "../../home/Home";
import SignIn from "../signIn/SignIn";
import ResetPass from "../resetPass/ResetPass";

// =================================================================
const useStyles = createUseStyles({
  SignUp: {
    minHeight: "100vh",
  },
  signup__title: {
    fontSize: "5rem",
    marginBottom: "5rem",
  },
  signup__separate: {
    marginBottom: "2rem",
    fontSize: "1.5rem",
    color: "var(--text-color)",
  },
  signup__link: {
    display: "flex",
    alignItems: "center",
    gap: "0.5rem",
    marginTop: "2rem",
    color: "var(--text-color)",
    fontSize: "1.6rem",
  },
  signup__link__light: {
    color: "var(--text-color-light) !important",
  },
  signup__input: {
    fontSize: "1.6rem",
    marginBottom: "2rem",
  },
  signup__error: {
    color: "red",
    textAlign: "left",
    fontSize: "1.3rem",
    marginBottom: "2rem",
  },
});

export default function SignUp({ Register, error }) {
  const classes = useStyles();

  const [details, setDetails] = useState({
    username: "",
    password: "",
    rePassword: "",
  });
  const [emptyTags, setEmptyTags] = useState([]);

  const validateRegister = () => {
    for (const item in details) {
      if (details[item] === "" && !emptyTags.includes(item)) {
        em
      }
    }
  };
  const checkRePassword = () => {
    if (details.password === details.rePassword) {
      return true;
    }
  };

  const submitRegister = (e) => {
    e.preventDefault();

    if (validateRegister()) {
      Register(details);
    }
  };

  return (
    <div className={classes.signup}>
      <div className="content">
        <h1 className={classes.signup__title}>Sign Up.</h1>

        <div className="buttons w-100">
          <button className="button btn--border btn--hover-border btn--full-width btn--flex  btn--hover-trans">
            <div className="button__icon">
              <ion-icon name="logo-google"></ion-icon>
            </div>
            <div className="button__text">Continue with Google</div>
          </button>
          <button className="button btn--border btn--hover-border btn--full-width btn--flex  btn--hover-trans">
            <div className="button__icon">
              <ion-icon name="logo-facebook"></ion-icon>
            </div>
            <div className="button__text">Continue with Facebook</div>
          </button>
        </div>

        <div className={classes.signup__separate}>or</div>

        <form onSubmit={submitRegister}>
          <div className="inputs w-100">
            <input
              type="text"
              placeholder="Username"
              className={`${classes.signup__input} input--border input--focus-border btn--full-width`}
              id="username"
              name="username"
              onChange={(e) =>
                setDetails({ ...details, username: e.target.value })
              }
              value={details.username}
            />
            {emptyTags.includes("username") ? (
              <div className={clsx(classes.input__mess)}>* Username cant empty</div>
            ) : (
              ""
            )}

            <input
              type="password"
              placeholder="Password"
              className={`${classes.signup__input} input--border input--focus-border btn--full-width`}
              id="password"
              name="password"
              onChange={(e) =>
                setDetails({ ...details, password: e.target.value })
              }
              value={details.password}
            />
            {emptyTags.includes("password") ? (
              <div className={clsx(classes.input__mess)}>* Password cant empty</div>
            ) : (
              ""
            )}
            <input
              type="password"
              placeholder="Re-Password"
              className={`${classes.signup__input} input--border input--focus-border btn--full-width`}
              id="rePassword"
              name="rePassword"
              onChange={(e) =>
                setDetails({ ...details, rePassword: e.target.value })
              }
              value={details.rePassword}
            />
            {emptyTags.includes("rePassword") ? (
              <div className={clsx(classes.input__mess)}>* rePassword cant empty</div>
            ) : (
              ""
            )}

            <button
              type="submit"
              className="button btn--bg-pink btn--bb-pink btn--none-border w-100"
            >
              <span className="button__text">Sign Up</span>
            </button>
            {error !== "" ? (
              <div className={classes.signin__error}>*{error}</div>
            ) : (
              ""
            )}
          </div>
        </form>

        <div className={classes.signup__link}>
          <span>Already have an account?</span>
          <Link to="/users/signIn" className={classes.signup__link__light}>
            Sign in
          </Link>
        </div>

        <Link to="/" className={classes.signup__link}>
          <ion-icon name="arrow-back-outline"></ion-icon>
          <div>Back to Home</div>
        </Link>
      </div>
    </div>
  );
}
