import React from "react";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

import EditTask from "../actions/editTask/EditTask";
import DeleteTask from "../actions/deleteTask/DeleteTask";

const useStyles = createUseStyles({
  task__bar: {
    borderRadius: "1.3rem",
    display: "flex",
    flexDirection: "column",
    gap: "1rem",
    width: "100%",
    position: "relative",
    minHeight: "15rem",
    border: "none !important",
  },
  task__btns: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    position: "relative",
    padding: "1rem",
    borderBottom: "2px solid pink",
  },
  collection__actions: {
    minWidth: "max-content",
    top: "101%",
    right: "4%",
    borderBottomRightRadius: "1.1rem",
    borderBottomLeftRadius: "1.1rem",
    backgroundColor: "#1e1e29",
    zIndex: "2",
    border: "2px solid pink",
    position: "absolute",
  },
  collection__action: {
    fontSize: "1.3rem",
    padding: "1.2rem 1.8rem",
  },
  task__title: {
    width: "100%",
    fontSize: "1.6rem",
    flexGrow: "1",
    padding: "0 1rem 1rem",
  },
  task__top: {
    width: "100%",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  task__checkbox: {
    position: "relative",
    width: "2rem",
    height: "2rem",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    borderRadius: "0.3rem",
    border: "1px solid var(--bg-btn-pink)",
  },
  task__input: {
    position: "absolute",
    width: "100%",
    height: "100%",
    opacity: "0",
  },
  task__selectIcon: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    width: "2rem",
    height: "2rem",
    borderRadius: "0.3rem",
    backgroundColor: "var(--bg-btn-pink)",
  },
});

const useActions = () => {
  const [editTaskDisplayVal, setEditTaskDisplayVal] = useState(false);
  const [deleteTaskDisplayVal, setDeleteTaskDisplayVal] = useState(false);

  // start - Taskection
  const toggleEditTaskDisplayVal = (value) => (event) => {
    setEditTaskDisplayVal(value);
  };
  const toggleDeleteTaskDisplayVal = (value) => (event) => {
    setDeleteTaskDisplayVal(value);
  };

  return {
    toggleEditTaskDisplayVal,
    toggleDeleteTaskDisplayVal,
    editTaskDisplayVal,
    deleteTaskDisplayVal,
  };
};

export default function Task({ task, handleChangeData, handleSelectTasks }) {
  const classes = useStyles();
  // console.log(task);

  const {
    toggleEditTaskDisplayVal,
    toggleDeleteTaskDisplayVal,
    editTaskDisplayVal,
    deleteTaskDisplayVal,
  } = useActions();

  const [displayActionsBarVal, setDisplayActionsBarVal] = useState(false);
  const toggelActionsBar = (e) => {
    setDisplayActionsBarVal(!displayActionsBarVal);
  };

  const [checkboxSelected, setCheckboxSelected] = useState(false);
  const handleSelectionChange = (e) => {
    console.log(e.target.checked);
    setCheckboxSelected(e.target.checked);
  };

  return (
   <div></div>
  );
}
