import React from "react";

const useStyles = createUseStyles({
    App: {
      backgroundImage: "url(https://tsks.app/img/signin_balls.png)",
      backgroundColor: "#181820",
      backgroundPosition: "top",
      backgroundSize: "contain",
      backgroundRepeat: "no-repeat",
    },
  });

export default function AddTask() {
  return (
    <div
      className="doing-tasks__bar position-absolute align-items-center "
      style={{
        display: "flex",
        top: "150%",
        right: "0",
        width: "30rem",
        padding: "0 1.3rem",
        borderRadius: "1.3rem",
        border: "3px solid var(--background-color-header)",
      }}
    >
      <div
        className="doing-tasks__button d-flex"
        style={{
          padding: "0.3rem",
          borderRadius: "0.7rem",
        }}
      >
        <ion-icon
          name="add"
          style={{
            fontSize: "1.7rem",
            cursor: "pointer",
          }}
        ></ion-icon>
      </div>
      <div className="doing-tasks__input w-100">
        <input
          type="text"
          placeholder="Add task"
          className="w-100 input--none-border"
          style={{
            fontSize: "1.6rem",
          }}
        />
      </div>
      <div className="doing-tasks__button">
        <ion-icon
          name="close"
          style={{
            fontSize: "2rem",
            cursor: "pointer",
          }}
        ></ion-icon>
      </div>
    </div>
  );
}
