// lib
import React from "react";
import { BrowserRouter as Router } from "react-router-dom";
import ReactDOM from "react-dom";
import "./index.css";
import App from "./App";
import reportWebVitals from "./reportWebVitals";
import axios from "axios";
import { toast } from "react-toastify";

import fetchClient from "./service/defaultAPI/fetchClient";
import setAuthToken from "./service/defaultAPI/setAuthToken";

axios.defaults.baseURL = "https://mvn-task-manager.work/";

axios.interceptors.request.use(
  function (config) {
    // Do something before request is sent
    let user = JSON.parse(localStorage.getItem("user"));
    console.log(user.token);
    axios.defaults.headers.common["Authorization"] =
      user && user.token ? `Bearer ${user.token}` : "";

    return config;
  },
  function (error) {
    console.log(error);
    // Do something with request error
    return Promise.reject(error);
  }
);

// // Add a response interceptor
axios.interceptors.response.use(
  function (response) {
    // Any status code that lie within the range of 2xx cause this function to trigger
    // Do something with response data
    return response;
  },
  function (error) {
    if (error.response.status == 401) {
      toast.warn("This token is invalidated");
      localStorage.removeItem("user")
    }

    // Any status codes that falls outside the range of 2xx cause this function to trigger
    // Do something with response error
    return Promise.reject(error);
  }
);
// fetchClient()

ReactDOM.render(
  <React.StrictMode>
    <Router>
      <App />
    </Router>
  </React.StrictMode>,
  document.getElementById("root")
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
