import React from "react";

function CollectionBar(props) {
  const collection = props.data;

  return (
    <div
      className="collection__bar d-flex w-100"
      style={{
        border: "1px solid red",
      }}
    >
      <div
        className="collection__back-btn"
        style={{
          border: "1px solid red",
          backgroundColor : 'var()'
        }}
      >
        <ion-icon name="chevron-back-outline"></ion-icon>
      </div>
      <div className="collection__name">{collection.cName}</div>
      <div className="collection__dots ml-auto position-relative">
        <ion-icon name="ellipsis-horizontal"></ion-icon>
        <div className="collection__actions position-absolute">
          <div className="collection__action">edit collection</div>
          <div className="collection__action">delete collection</div>
        </div>
      </div>
    </div>
  );
}
function AddTask() {
  return (
    <div
      className="add-task__tab  w-100"
      style={{
        border: "1px solid red",
      }}
    >
      <div className="add-task__title">Add a task</div>
      <div className="add-task__bar">
        <div className="add-task__button">
          <ion-icon name="add"></ion-icon>
        </div>
        <div className="add-task__input">
          <input type="text" placeholder="Add task" />
        </div>
        <div className="add-task__button">
          <ion-icon name="close"></ion-icon>
        </div>
      </div>
    </div>
  );
}
function DoingTasks() {
  return (
    <div className="doing-tasks w-50">
      <div className="doing-tasks__title">Doing Tasks</div>
      <div className="doing-tasks__bar">
        <div className="doing-tasks__button">
          <input type="checkbox" />
        </div>
        <div className="doing-tasks__name">{"fjfdhb"}</div>
        <div className="doing-tasks__button">
          <ion-icon name="close"></ion-icon>
        </div>
      </div>
    </div>
  );
}
function CompletedTasks() {
  return (
    <div className="doing-tasks w-50">
      <div className="add-task__title">Add a task</div>
      <div className="add-task__bar">
        <div className="add-task__button">
          <ion-icon name="add"></ion-icon>
        </div>
        <div className="add-task__input">
          <input type="text" placeholder="Add task" />
        </div>
        <div className="add-task__button">
          <ion-icon name="close"></ion-icon>
        </div>
      </div>
    </div>
  );
}
export default function ViewCollection(props) {
  const collection = props.task;

  return (
    <div className="ViewCollection">
      <div
        className="content d-flex flex-column"
        style={{
          textAlign: "left",
          maxWidth: "70rem",
          minWidth: "70rem",
          border: "1px solid red",
        }}
      >
        <CollectionBar data={collection} />
        <AddTask />

        <div className="tasks__tab d-flex w-100">
          <DoingTasks />
          <CompletedTasks />
        </div>
      </div>
    </div>
  );
}
