// lib
import { Link } from "react-router-dom";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

// Component
import AddTask from "../../task/addTask/AddTask";
import SearchTasks from "../../task/searchTask/SearchTask";

// Service

const useStyles = createUseStyles({
  collection__backBtn: {
    padding: "1.2rem",
    marginRight: "1.5rem",
    borderRadius: "1.5rem",
    backgroundColor: "var(--background-color-header)",
    cursor: "pointer",
  },
  collection__name: {
    fontSize: "3rem",
    textTransform: "capitalize",
  },
  taskActions: {
    gap: "1rem",
    marginRight: "1rem",
  },
  taskAction: {
    marginRight: "1rem",
    position: "relative",
  },
  collection__dots: {
    cursor: "pointer",
    position: "relative",
  },
  collection__actions: {
    minWidth: "max-content",
    right: "0",
    bottom: "0",
    borderRadius: "1.1rem",
    backgroundColor: "#1e1e29",
    zIndex: "2",
    position: "absolute",
  },
  collection__action: {
    padding: "2rem 4rem",
  },
});

export default function CollectionBar({ collection, handleChange }) {
  const navigate = useNavigate();
  const classes = useStyles();

  const [displayAddBarVal, setDisplayAddBarVal] = useState(false);
  const [displaySearchBarVal, setDisplaySearchBarVal] = useState(false);

  const toggleAddBar = () => (event) => {
    setDisplayAddBarVal(!displayAddBarVal);
    setDisplaySearchBarVal(false);
  };
  const toggleSearchBar = () => (event) => {
    setDisplaySearchBarVal(!displaySearchBarVal);
    setDisplayAddBarVal(false);
  };

  return (
    <div className="collection__bar d-flex align-items-center w-100">
      <Link className={clsx(classes.collection__backBtn)} to="/">
        <ion-icon name="chevron-back-outline"></ion-icon>
      </Link>
      <div className={clsx(classes.collection__name, "mr-auto")}>
        {collection.name}
      </div>

      <div className={clsx(classes.taskActions, "d-flex align-items-center")}>
        <div className={clsx(classes.taskAction)} onClick={toggleAddBar()}>
          <div className="taskAction__icon">
            <ion-icon name="add"></ion-icon>
          </div>
          {displayAddBarVal && <AddTask handleChange={handleChange} />}
        </div>

        <div className={clsx(classes.taskAction)} onClick={toggleSearchBar()}>
          <div className="taskAction__icon">
            <ion-icon name="search"></ion-icon>
          </div>
          {displaySearchBarVal && <SearchTasks handleChange={handleChange} />}
        </div>
      </div>

      <div className={clsx(classes.collection__dots)}>
        <ion-icon name="ellipsis-horizontal"></ion-icon>
        <div className={clsx(classes.collection__actions)}>
          <div
            className={clsx(
              classes.collection__action,
              "button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            )}
          >
            edit collection
          </div>
          <div
            className={clsx(
              classes.collection__action,
              "button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            )}
          >
            delete collection
          </div>
        </div>
      </div>
    </div>
  );
}
