import React from "react";

import TaskBar from "./TaskBar";
import Task from "./Task";

export default function ViewTask({ listTasks, status }) {
  useEffect(() => {
    axios
      .get(`/api/tasks?limit=8&page=1`)
      .then((res) => {
        console.log(res.data);
        setListTasks(res.data.items);
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
      });
  }, []);
  const handleChange = (tasksData) => {
    if (IsEmptyObject(tasksData)) {
      console.log("Khum co");
    } else {
      console.log("set data tasks");
      // setCollection(tasksData);
    }
  };
  return (
    <div
      className="task__tab"
      style={{
        border: "1px solid red",
        marginTop: "3rem",
      }}
    >
      <TaskBar status={status} count={listTasks.length} />

      <div className="tasks">
        {listTasks.map((task) => {
          return <Task key={task.id} task={task} />;
        })}
      </div>
    </div>
  );
}
