// lib
import { createUseStyles } from "react-jss";
import clsx from "clsx";
import { useState, useEffect } from "react";
import axios from "axios";

import useTaskForm from "../services/useTaskForm";
import GetCollectionFunc from "../GetTasksFunc";

const useStyles = createUseStyles({
  EditTask: {
    top: "0",
    left: "0",
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    zIndex: "11",
    color: "#FFFFFF",
  },
  editTask__bg: {
    backgroundColor: "#000",
    opacity: "70%",
  },
  editTask__container: {
    width: "42rem",
    minWidth: "48rem",
    marginTop: "6rem",
    padding: "2rem",
    backgroundColor: "#1D1D27",
    textAlign: "left",
    borderRadius: "1.6rem",
  },
  editTask__caption: {
    fontSize: "2rem",
    textTransform: "capitalize",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  editTask__title: {
    fontSize: "1.6rem",
    textTransform: "capitalize",
    marginBottom: "1rem",
  },
  editTask__colors: {
    display: "grid",
    gap: "1.5rem",
    gridTemplateColumns: "repeat(auto-fit, 6rem)",
  },
  editTask__color: {
    width: "6rem",
    height: "5rem",
    backgroundColor: "transparent",
    cursor: "pointer",
    borderRadius: "1.2rem",
    transition: "all 0.1s ease-in-out",
    position: "relative",
  },
  editTask__buttons: {
    gap: "1rem",
    marginTop: "5rem",
  },
  editTask__group: {
    marginBottom: "2rem",
    width: "100%",
  },
  editTask__err: {
    color: "red",
    padding: "1rem",
    fontSize: "1.4rem",
  },
});

const EditTask = ({ listColl, task, toggleFunc, displayVal }) => {
  const classes = useStyles();
  
  const HandleEditTask = (values) => {
    console.log(values);

    const title = values.title;
    axios({
      method: "post",
      url: "api/categories/${collection.id}",
      data: { title },
    })
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
      });
  };
  const [selectedOption, setSelectedOption] = useState("");

  const { handleChange, handleSubmit, values, errors } =
    useTaskForm(HandleEditTask);

  const closeTabFunc = () => (event) => {
    toggleFunc(false)();
  };

  const handleChangeOption = (event) => {
    const optionVal = event.target.value;
    setSelectedOption(optionVal);
    console.log(optionVal);

    if (optionVal != "") {
    }
  };

  return (
    <div
      className={clsx(
        classes.EditTask,
        "m-auto position-fixed",
        !displayVal && "d-none"
      )}
    >
      <div
        className={clsx(classes.editTask__bg, "w-100 h-100 position-absolute")}
        onClick={closeTabFunc(false)}
      ></div>

      <div className={clsx(classes.editTask__container, "position-absolute")}>
        <form method="post" onSubmit={handleSubmit}>
          <div className="editTask__top d-flex w-100">
            <div className={clsx(classes.editTask__caption, "mr-auto")}>
              Edit Task
            </div>
            <div className="editTask__close-btn" onClick={closeTabFunc(false)}>
              <ion-icon name="close"></ion-icon>
            </div>
          </div>

          <div className={clsx(classes.editTask__group, "bar-title")}>
            <div className={clsx(classes.editTask__title)}>Title</div>
            <input
              type="text"
              placeholder="My Task"
              className="btn--outline w-100 py-3"
              style={{ fontSize: "1.6rem" }}
              name="title"
              value={values.title}
              onChange={handleChange}
            />
            {errors.title && (
              <div className={clsx(classes.editTask__err)}>*{errors.title}</div>
            )}
          </div>
          <div className={clsx(classes.editTask__group, "bar-listColl")}>
            <div className={clsx(classes.editTask__listColl)}>
              List Collection
            </div>
            <select
              className="form-control-lg"
              aria-label="Default select example"
              onChange={handleChangeOption}
              value={selectedOption}
            >
              <option value="" className="taskBar__option">
                Choose Collection
              </option>
              {listColl &&
                listColl.map((item) => {
                  return (
                    <option
                      key={item.id}
                      value={item.id}
                      className="taskBar__option"
                    >
                      {item.name}
                    </option>
                  );
                })}
            </select>
          </div>

          <div className={clsx(classes.editTask__buttons, "w-100 d-flex")}>
            <button
              className="editTask__button button btn--none-border btn--hover-bg-gray-light ml-auto mb-0"
              style={{ backgroundColor: "#3D3C50" }}
              type="submit"
            >
              Submit
            </button>
            <button
              type="reset"
              className="editTask__button button btn--none-border btn--hover-bg-gray mb-0"
              onClick={closeTabFunc()}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditTask;
