import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";
import axios from "axios";

const useStyles = createUseStyles({
  addTasks__bar: {
    display: "flex",
    top: "150%",
    right: "0",
    width: "30rem",
    padding: "0 1.3rem",
    borderRadius: "1.3rem",
    border: "3px solid var(--background-color-header)",
    position: "absolute",
    alignItems: "center",
    zIndex: "12"
  },
  addTasks__button: {
    padding: "0.3rem",
    borderRadius: "0.7rem",
    display: "flex",
  },
});

export default function AddTask({ collection, handleChange }) {
  const classes = useStyles();

  const [title, setTitle] = useState("");
  const categoryIds = [collection.id];

  const createTask = () => {
    axios({
      method: "post",
      url: "api/tasks",
      data: { title, categoryIds },
    })
      .then((res) => {
        console.log(res);
        console.log("Create task successfully");

        axios
          .get(`api/categories/${collection.id}`)
          .then((result) => {
            handleChange(result.data);
          })
          .catch((error) => {
            const errorMsg = error.response.data.message;
            console.log(errorMsg);
          });
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
      });
  };

  return (
    <div className={clsx(classes.addTasks__bar)}>
      <div className={clsx(classes.addTasks__button)} onClick={createTask}>
        <ion-icon
          name="add"
          style={{
            fontSize: "1.7rem",
          }}
        ></ion-icon>
      </div>
      <div className="addTasks__input w-100">
        <input
          type="text"
          placeholder="Add task"
          className="w-100 input--none-border"
          style={{
            fontSize: "1.6rem",
          }}
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </div>
      <div className="addTasks__button" onClick={() => setTitle("")}>
        <ion-icon
          name="close"
          style={{
            fontSize: "2rem",
          }}
        ></ion-icon>
      </div>
    </div>
  );
}
