import React from "react";
import axios from "axios";
import { toast } from "react-toastify";

export default async function GetUser(id) {
  const linkApi = `api/users/${id}`;
  let user = {};

  // TODO
  axios
    .get(linkApi)
    .then((res) => {
      user = res.data;
    })
    .catch((err) => {
      const errMsg = err.response.data.message;
      toast.error(errMsg);
    });
console.log(object);
  return user;
}
