import React from "react";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

const useStyles = createUseStyles({
  taskBar__actions: {
    display: "flex",
  },
});

export default function TaskBar({
  status,
  count,
  handleChangeData,
  handleChangeStatus,
}) {
  const classes = useStyles();

  const [listStatus, setListStatus] = useState([
    { name: "All Task", value: "all" },
    { name: "In Process Task", value: "inProcess" },
    { name: "Complete Task", value: "complete" },
  ]);
  const [selectedOption, setSelectedOption] = useState(status);

  function handleChangeOption(event) {
    const optionVal = event.target.value;
    setSelectedOption(optionVal);
    console.log(optionVal);
  }

  return (
    <div
      className="task__title"
      style={{
        fontSize: "2rem",
        textTransform: "capitalize",
        padding: "2rem",
        display: "flex",
        justifyContent: "space-between",
      }}
    >
      <div className="taskBar__details">Total : {count}</div>

      <div className="taskBar__actions">
        <div className="taskBar__action">
          <ion-icon name="trash"></ion-icon>
        </div>
        <div className="taskBar__action">
          <select
            className="form-control-lg"
            aria-label="Default select example"
            onChange={handleChangeOption}
            value={selectedOption}
          >
            {listStatus.map((item) => {
              return (
                <option
                  key={item.value}
                  value={item.value}
                  className="taskBar__option"
                >
                  {item.name}
                </option>
              );
            })}
          </select>
        </div>
      </div>
    </div>
  );
}
