// lib
import React from "react";
import { createUseStyles } from "react-jss";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

// Component
import Header from "./components/header/Header";
import Routers from "./routers/Routers";

// Service
import ToggleUserBar from "./service/header/ToggleUserBar";

// Css
import "./App.css";

// =================================================================
const useStyles = createUseStyles({
  App: {
    backgroundImage: "url(https://tsks.app/img/signin_balls.png)",
    backgroundColor: "#181820",
    backgroundPosition: "top",
    backgroundSize: "contain",
    backgroundRepeat: "no-repeat",
  },
});

function App() {
  const navigate = useNavigate();
  const classes = useStyles();
  const [user, setUser] = useState({ username: "", id: "" });
  const { handleToggleUserBar, toggleUserBarValue } = ToggleUserBar();

  // Function
  const HandleLogin = (details) => {
    setUser({
      username: details.username,
      id: details.id,
    });

    // Redirect
    navigate("");

    console.log("Logged in successfully");
  };
  const HandleLogout = () => {
    setUser({
      username: "",
      id: "",
    });

    localStorage.removeItem("auth_token");
    localStorage.removeItem("auth_id");

    // Redirect
    navigate("");

    console.log("Logout in successfully");
  };

  useEffect(() => {
    let auth_id = localStorage.getItem("auth_id");
    if (auth_id) {
      axios
        .get(`api/users/${auth_id}`)
        .then((res) => {
          setUser({
            username: res.data.username,
            id: res.data.id,
          });
        })
        .catch((err) => {
          const errMsg = err.response.data.message;
          console.log(errMsg);
        });
    }
  }, []);

  const handleClick = (e) => {
    const element = e.target;
    const idElement = e.target.id;


    if (idElement != "users__avt") {
      handleToggleUserBar(false);
    }
  };

  return (
    <div className={classes.App} onClick={handleClick}>
      <Header HandleLogout={HandleLogout} user={user} />

      <Routers HandleLogin={HandleLogin} user={user} />
    </div>
  );
}
export default App;
