import React from "react";
import "./home.css";

import HeaderHome from "./headerHome/HeaderHome";

function HasNoCollection(props) {
  return (
    <div className="HasNoCollection">
      <div
        className="content"
        style={{
          textAlign: "left",
          maxWidth: `${props.data.length > 0 ? `70rem` : `40rem`}`,
          minWidth: `${props.data.length > 0 ? `70rem` : `40rem`}`,
        }}
      >
        <div
          className="title"
          style={{
            fontSize: "3rem",
            fontWeight: "bold",
            margin: "2rem 0",
          }}
        >
          {props.data.length > 0 ? "Collections" : "You have no collections."}
        </div>

        <div
          className="collections"
          style={{
            width: "100%",
            display: "grid",
            gridTemplateColumns: `repeat(${
              props.data.length > 0 ? "2" : "1"
            },1fr)`,
            gap: "1.7rem",
          }}
        >
          {props.data &&
            props.data.length > 0 &&
            props.data.map((collection) => {
              console.log(collection.cId);
              return (
                <div
                  className="collection d-flex"
                  style={{
                    padding: "2.2rem 2.5rem",
                    borderRadius: "2rem",
                    gap: "2rem",
                    backgroundColor: "var(--background-color-header)",
                  }}
                  key={collection.id}
                >
                  <div
                    className="collection__tag d-flex"
                    style={{
                      padding: "1.3rem",
                      borderRadius: "1.5rem",
                      backgroundColor: `${collection.cColor}`,
                    }}
                  >
                    <ion-icon
                      name="bowling-ball-outline"
                      style={{
                        fontSize: "3.2rem",
                      }}
                    ></ion-icon>
                  </div>
                  <div className="collection__details">
                    <div
                      className="collection__name"
                      style={{
                        width: "fit-content",
                        textAlign: "left",
                        fontSize: "2rem",
                        textTransform: "capitalize",
                      }}
                    >
                      {collection.cName}
                    </div>
                    <div
                      className="collection__count-tasks"
                      style={{
                        color: "#A1A1A6",
                        fontSize: "1.6rem",
                      }}
                    >
                      No tasks
                    </div>
                  </div>
                </div>
              );
            })}

          <button
            className="addCollection button btn--flex btn--border btn--hover-bg-gray"
            style={{
              height: "fit-content",
              margin: "auto 0",
              borderRadius: "1.5rem",
            }}
          >
            <ion-icon name="add-outline"></ion-icon>
            {props.data.length > 0
              ? "Add Collections"
              : "Add Your First Collection"}
          </button>
        </div>
      </div>
    </div>
  );
}

export default function Home() {
  let collections = [
    {
      cId: 1,
      cName: "lorem ipsum dolor sit amet, consectetur adip",
      cColor: "yellow",
    },
    { cId: 2, cName: "abc", cColor: "red" },
    { cId: 3, cName: "abc", cColor: "pink" },
    { cId: 4, cName: "abc", cColor: "coral" },
    { cId: 5, cName: "abc", cColor: "green" },
  ];
  return (
    <div className="Home">
      <HeaderHome />
      <div
        className="content"
        style={{
          maxWidth: "max-content",
        }}
      >
        <HasNoCollection data={collections} />
      </div>
    </div>
  );
}
