// lib
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

// Component
import CollectionBar from "./CollectionBar";



export default function ViewCollection() {
  let { cId } = useParams();

  const [collection, setCollection] = useState({});

  useEffect(() => {
    if (cId) {
      axios
        .get(`api/categories/${cId}`)
        .then((res) => {
          setCollection(res.data);
        })
        .catch((err) => {
          const errMsg = err.response.data.message;
          console.log(errMsg);
        });
    }
  }, []);

  const handleChange = (data) => {
    setCollection(data);
  };

  return (
    <div className="ViewCollection">
      <div
        className="content d-flex flex-column"
        style={{
          textAlign: "left",
          maxWidth: "70rem",
          minWidth: "70rem",
        }}
      >
        <div className="tasks__tab d-flex justify-content-between w-100 mt-5">
          <CollectionBar collection={collection} handleChange={handleChange} />
        </div>
      </div>
    </div>
  );
}
