// lib
import React from "react";
import { createUseStyles } from "react-jss";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";

// Component
// import Home from "./components/home/Home";

// =================================================================
const useStyles = createUseStyles({
  SignIn: {
    minHeight: "100vh",
  },
  signin__title: {
    fontSize: "5rem",
    marginBottom: "5rem",
  },
  signin__separate: {
    marginBottom: "2rem",
    fontSize: "1.5rem",
    color: "var(--text-color)",
  },
  signin__link: {
    display: "flex",
    alignItems: "center",
    gap: "0.5rem",
    marginTop: "2rem",
    color: "var(--text-color)",
    fontSize: "1.6rem",
  },
  signin__link__light: {
    color: "var(--text-color-light) !important",
  },
  signin__input: {
    fontSize: "1.6rem",
    marginBottom: "2rem",
  },
});

export default function SignIn() {
  const classes = useStyles();

  return (
    <div className={classes.SignIn}>
      <div className="content">
        <h1 className={classes.signin__title}>Sign In.</h1>

        <div className="buttons w-100">
          <button className="button btn--border btn--hover-border btn--full-width btn--flex btn--hover-trans">
            <div className="button__icon">
              <ion-icon name="logo-google"></ion-icon>
            </div>
            <div className="button__text">Continue with Google</div>
          </button>
          <button className="button btn--border btn--hover-border btn--full-width btn--flex btn--hover-trans">
            <div className="button__icon">
              <ion-icon name="logo-facebook"></ion-icon>
            </div>
            <div className="button__text">Continue with Facebook</div>
          </button>
        </div>

        <div className={classes.signin__separate}>or</div>

        <form method="POST" action="login">
          <div className="inputs w-100">
            <input
              type="email"
              placeholder="Email"
              className={
                classes.signin__input +
                " input--border input--focus-border btn--full-width"
              }
            />
            <input
              type="password"
              placeholder="Password"
              className={
                classes.signin__input +
                " input--border input--focus-border btn--full-width"
              }
            />

            <button className="button btn--bg-pink btn--bb-pink btn--none-border w-100">
              <span className="button__text">Sign Up</span>
            </button>
          </div>
        </form>

        <div className={classes.signin__link}>
          <span>Don't have an account?</span>
          <Link to = "" className={classes.signin__link__light}>
            Sign up
          </Link>
        </div>

        <a
          href="#"
          className={classes.signin__link + " " + classes.signin__link__light}
        >
          Forgot password?
        </a>

        <a href="#" className={classes.signin__link}>
          <ion-icon name="arrow-back-outline"></ion-icon>
          <div>Back to Home</div>
        </a>
      </div>
    </div>
  );
}
