// lib
import { Routes, Route, Link, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import clsx from "clsx";
import axios from "axios";
import { useNavigate } from "react-router-dom";

// Component
import AddTask from "../../task/addTask/AddTask";
import SearchTasks from "../../task/searchTask/SearchTask";

// Service

export default function CollectionBar({ collection, handleChange }) {
  const navigate = useNavigate();

  const [displayAddBarVal, setDisplayAddBarVal] = useState(false);

  const toggleAddBar = () => {
      
  }
  const toggleSearchBar = () => {

  }

  return (
    <div className="collection__bar d-flex align-items-center w-100">
      <div
        className="collection__back-btn"
        style={{
          padding: "1.2rem",
          marginRight: "1.5rem",
          borderRadius: "1.5rem",
          backgroundColor: "var(--background-color-header)",
          cursor: "pointer",
        }}
        onClick={() => navigate("")}
      >
        <ion-icon
          name="chevron-back-outline"
          style={{
            fontSize: "2.7rem",
          }}
        ></ion-icon>
      </div>
      <div
        className="collection__name mr-auto"
        style={{
          fontSize: "3rem",
          textTransform: "capitalize",
        }}
      >
        {collection.name}
      </div>

      <div
        className="taskActions d-flex align-items-center"
        style={{
          gap: "1rem",
          marginRight: "1rem",
        }}
      >
        <div className="taskAction position-relative">
          <div className="taskAction__icon">
            <ion-icon name="add"></ion-icon>
          </div>
          {<AddTask handleChange={handleChange} />}
        </div>

        <div className="taskAction position-relative">
          <div className="taskAction__icon">
            <ion-icon name="search"></ion-icon>
          </div>
          {<SearchTasks handleChange={handleChange} />}
        </div>
      </div>

      <div
        className="collection__dots position-relative"
        style={{
          cursor: "pointer",
        }}
      >
        <ion-icon
          name="ellipsis-horizontal"
          style={{
            fontSize: "2.5rem",
          }}
        ></ion-icon>
        <div
          className="collection__actions position-absolute d-none"
          style={{
            minWidth: "max-content",
            right: "0",
            borderRadius: "1.1rem",
            backgroundColor: "#1e1e29",
            zIndex: "2",
          }}
        >
          <div
            className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            style={{
              width: "20rem",
            }}
          >
            edit collection
          </div>
          <div
            className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            style={{
              width: "20rem",
            }}
          >
            delete collection
          </div>
        </div>
      </div>
    </div>
  );
}
