// lib
import { useParams } from "react-router-dom";
import { useState, useEffect, useLayoutEffect } from "react";
import { createUseStyles } from "react-jss";
import axios from "axios";
import clsx from "clsx";

// Component
import CollectionBar from "./CollectionBar";
import ViewTask from "../../task/view/ViewTask";
import InProcessTasks from "../../task/view/InProcessTasks";
import CompletedTasks from "../../task/view/CompleteTasks";

// Service
import IsEmptyObject from "../../../../service/IsEmptyObject";

const useStyles = createUseStyles({
  content: {
    textAlign: "left",
    maxWidth: "unset",
  },
});

export default function ViewCollection() {
  const classes = useStyles();
  let { cId } = useParams();

  const [data, setData] = useState({});
  const [collection, setCollection] = useState({});

  useEffect(() => {
    if (cId) {
      axios
        .get(`api/categories/${cId}`)
        .then((res) => {
          setCollection(res.data);
        })
        .catch((err) => {
          const errMsg = err.response.data.message;
          console.log(errMsg);
        });
    }
  }, []);

  useEffect(async () => {
    const tasksData = await SearchTaskFunc(4);
    handleChangeData(tasksData.data);
  }, []);

  const handleChangeData = (data) => {
    console.log(data);
    setData(data);
  };

  return (
    <div className="ViewCollection">
      <div className={clsx(classes.content, "content")}>
        <CollectionBar
          collection={collection}
          handleChangeData={handleChangeData}
        />

        <ViewTask data={data} handleChangeData={handleChangeData} />
      </div>
    </div>
  );
}
