// lib
import { Link } from "react-router-dom";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { createUseStyles } from "react-jss";

// Component
import AddTask from "../../task/addTask/AddTask";
import SearchTasks from "../../task/searchTask/SearchTask";

// Service

const useStyles = createUseStyles({
    collection__backBtn:{
        padding: "1.2rem",
        marginRight: "1.5rem",
        borderRadius: "1.5rem",
        backgroundColor: "var(--background-color-header)",
        cursor: "pointer",
    }
});

export default function CollectionBar({ collection, handleChange }) {
  const navigate = useNavigate();
  const classes = useStyles();

  const [displayAddBarVal, setDisplayAddBarVal] = useState(false);
  const [displaySearchBarVal, setDisplaySearchBarVal] = useState(false);

  const toggleAddBar = () => (event) => {
    setDisplayAddBarVal(!displayAddBarVal);
    setDisplaySearchBarVal(false);
  };
  const toggleSearchBar = () => (event) => {
    setDisplaySearchBarVal(!displaySearchBarVal);
    setDisplayAddBarVal(false);
  };

  return (
    <div className="collection__bar d-flex align-items-center w-100">
      <Link
        className=""
        to="/"
      >
        <ion-icon
          name="chevron-back-outline"
          style={{
            fontSize: "2.7rem",
          }}
        ></ion-icon>
      </Link>
      <div
        className="collection__name mr-auto"
        style={{
          fontSize: "3rem",
          textTransform: "capitalize",
        }}
      >
        {collection.name}
      </div>

      <div
        className="taskActions d-flex align-items-center"
        style={{
          gap: "1rem",
          marginRight: "1rem",
        }}
      >
        <div className="taskAction position-relative" onClick={toggleAddBar()}>
          <div className="taskAction__icon">
            <ion-icon name="add"></ion-icon>
          </div>
          {displayAddBarVal && <AddTask handleChange={handleChange} />}
        </div>

        <div
          className="taskAction position-relative"
          onClick={toggleSearchBar()}
        >
          <div className="taskAction__icon">
            <ion-icon name="search"></ion-icon>
          </div>
          {displaySearchBarVal && <SearchTasks handleChange={handleChange} />}
        </div>
      </div>

      <div
        className="collection__dots position-relative"
        style={{
          cursor: "pointer",
        }}
      >
        <ion-icon
          name="ellipsis-horizontal"
          style={{
            fontSize: "2.5rem",
          }}
        ></ion-icon>
        <div
          className="collection__actions position-absolute d-none"
          style={{
            minWidth: "max-content",
            right: "0",
            borderRadius: "1.1rem",
            backgroundColor: "#1e1e29",
            zIndex: "2",
          }}
        >
          <div
            className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            style={{
              width: "20rem",
            }}
          >
            edit collection
          </div>
          <div
            className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            style={{
              width: "20rem",
            }}
          >
            delete collection
          </div>
        </div>
      </div>
    </div>
  );
}
