import React from "react";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

const useStyles = createUseStyles({
  task__bar: {
    borderRadius: "1.3rem",
    display: "flex",
    flexDirection: "column",
    gap: "1rem",
    width: "100%",
    position: "relative",
    minHeight: "15rem",
    border: "none !important",
  },
  task__btns: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    position: "relative",
    padding: "1rem",
    borderBottom: "2px solid pink",
  },
  collection__actions: {
    minWidth: "max-content",
    top: "101%",
    right: "4%",
    borderBottomRightRadius: "1.1rem",
    borderBottomLeftRadius: "1.1rem",
    backgroundColor: "#1e1e29",
    zIndex: "2",
    border: "2px solid pink",
    position: "absolute",
  },
  collection__action: {
    fontSize: "1.3rem",
    padding: "1.2rem 1.8rem",
  },
  task__title: {
    width: "100%",
    fontSize: "1.6rem",
    flexGrow: "1",
    padding: "0 1rem 1rem",
  },
  task__top: {
    width: "100%",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  task__checkbox: {
    position: "relative",
    width: "2rem",
    height: "2rem",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    borderRadius: "0.3rem",
    border: "1px solid var(--bg-btn-pink)",
  },
  task__input: {
    position: "absolute",
    width: "100%",
    height: "100%",
    opacity: "0",
  },
  task__selectIcon: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    width: "2rem",
    height: "2rem",
    borderRadius: "0.3rem",
    backgroundColor: "var(--bg-btn-pink)",
  },
});

const useActions = () => {
  const [displayCollectionActionsBarVal, setDisplayCollectionActionsBarVal] =
    useState(false);
  const [displayAddBarVal, setDisplayAddBarVal] = useState(false);
  const [displaySearchBarVal, setDisplaySearchBarVal] = useState(false);

  const [editCollDisplayVal, setEditCollDisplayVal] = useState(false);
  const [deleteCollDisplayVal, setDeleteCollDisplayVal] = useState(false);

  // start - collection
  const toggleEditCollDisplayVal = (value) => (event) => {
    setEditCollDisplayVal(value);
  };
  const toggleDeleteCollDisplayVal = (value) => (event) => {
    setDeleteCollDisplayVal(value);
  };

  const toggleAddBar = () => {
    setDisplayAddBarVal(!displayAddBarVal);
    setDisplaySearchBarVal(false);
    setDisplayCollectionActionsBarVal(false);
  };
  const toggleSearchBar = () => {
    setDisplaySearchBarVal(!displaySearchBarVal);
    setDisplayAddBarVal(false);
    setDisplayCollectionActionsBarVal(false);
  };
  const toggleCollectionActionsBar = () => {
    setDisplayCollectionActionsBarVal(!displayCollectionActionsBarVal);
    setDisplayAddBarVal(false);
    setDisplaySearchBarVal(false);
  };

  return {
    toggleEditCollDisplayVal,
    toggleDeleteCollDisplayVal,
    editCollDisplayVal,
    deleteCollDisplayVal,
  };
};

export default function Task({ task, handleChangeData, handleSelectTasks }) {
  const classes = useStyles();
  // console.log(task);

  const {
    toggleEditTaskDisplayVal,
    toggleDeleteTaskDisplayVal,
    editTaskDisplayVal,
    deleteTaskDisplayVal,
  } = useActions();

  const [displayActionsBarVal, setDisplayActionsBarVal] = useState(false);
  const toggelActionsBar = (e) => {
    setDisplayActionsBarVal(!displayActionsBarVal);
  };

  const [checkboxSelected, setCheckboxSelected] = useState(false);
  const handleSelectionChange = (e) => {
    console.log(e.target.checked);
    setCheckboxSelected(e.target.checked);
  };

  return (
    <div
      className={clsx(classes.task__bar)}
      style={{
        border:
          task.status === "IN_PROGRESS"
            ? `3px solid var(--background-color-header)`
            : "3px solid pink",
        backgroundColor: "var(--background-color-header)",
      }}
    >
      <div className={clsx(classes.task__btns)}>
        <div className={clsx(classes.task__top)}>
          <div className={clsx(classes.task__checkbox)}>
            {checkboxSelected && (
              <div className={clsx(classes.task__selectIcon)}>
                <ion-icon name="checkmark"></ion-icon>
              </div>
            )}
            <input
              type="checkbox"
              className={clsx(classes.task__input)}
              onChange={handleSelectionChange}
            />
          </div>
          <ion-icon
            name="ellipsis-vertical"
            style={{ fontSize: "2rem", marginLeft: "auto" }}
            onClick={toggelActionsBar}
          ></ion-icon>
        </div>

        <div
          className={clsx(
            classes.collection__actions,
            !displayActionsBarVal && "d-none"
          )}
        >
          <div
            className={clsx(
              classes.collection__action,
              " button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            )}
          >
            edit task
          </div>
          <div
            className={clsx(
              classes.collection__action,
              " button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            )}
          >
            Submit Task
          </div>
          <div
            className={clsx(
              classes.collection__action,
              " button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
            )}
          >
            delete task
          </div>
        </div>
      </div>

      <div className={clsx(classes.task__title)}>{task.title}</div>

      <EditTask
        task={task}
        displayVal={editTaskDisplayVal}
        toggleFunc={toggleEditTaskDisplayVal}
      />
      <DeleteTask
        task={task}
        displayVal={deleteTaskDisplayVal}
        toggleFunc={toggleDeleteTaskDisplayVal}
      />
    </div>
  );
}
