import React from "react";

function CollectionBar(props) {
  const collection = props.data;

  return (
    <div className="collection__bar d-flex align-items-center w-100" style={{}}>
      <div
        className="collection__back-btn"
        style={{
          padding: "1.2rem",
          marginRight: "1.5rem",
          borderRadius: "1.5rem",
          backgroundColor: "var(--background-color-header)",
        }}
      >
        <ion-icon
          name="chevron-back-outline"
          style={{
            fontSize: "2.7rem",
          }}
        ></ion-icon>
      </div>
      <div
        className="collection__name"
        style={{
          fontSize: "3rem",
          textTransform: "capitalize",
        }}
      >
        {collection.cName}
      </div>
      <div className="collection__dots ml-auto position-relative">
        <ion-icon
          name="ellipsis-horizontal"
          style={{
            fontSize: "2.5rem",
          }}
        ></ion-icon>
        <div className="collection__actions position-absolute d-none">
          <div className="collection__action">edit collection</div>
          <div className="collection__action">delete collection</div>
        </div>
      </div>
    </div>
  );
}
function AddTask() {
  return (
    <div
      className="add-task__tab"
      style={{
        width: "48%",
      }}
    >
      <div
        className="add-task__title mb-4"
        style={{
          fontSize: "1.6rem",
          textTransform: "capitalize",
        }}
      >
        Add a task
      </div>
      <div
        className="add-task__bar w-100 d-flex align-items-center"
        style={{
          border: "1px solid red",
          
        }}
      >
        <div className="add-task__button">
          <ion-icon name="add"></ion-icon>
        </div>
        <div className="add-task__input w-100">
          <input type="text" placeholder="Add task" className="w-100 input--none-border" />
        </div>
        <div className="add-task__button">
          <ion-icon name="close"></ion-icon>
        </div>
      </div>
    </div>
  );
}
function SearchTasks() {
  return (
    <div
      className="add-task__tab"
      style={{
        width: "48%",
      }}
    >
      <div
        className="add-task__title mb-4"
        style={{
          fontSize: "1.6rem",
          textTransform: "capitalize",
        }}
      >
        Search tasks
      </div>
      <div className="add-task__bar">
        <div className="add-task__button">
          <ion-icon name="add"></ion-icon>
        </div>
        <div className="add-task__input">
          <input type="text" placeholder="Search tasks" className="w-100" />
        </div>
        <div className="add-task__button">
          <ion-icon name="close"></ion-icon>
        </div>
      </div>
    </div>
  );
}
function DoingTasks() {
  return (
    <div
      className="doing-tasks"
      style={{
        width: "48%",
      }}
    >
      <div
        className="doing-tasks__title mb-4"
        style={{
          fontSize: "1.6rem",
          textTransform: "capitalize",
        }}
      >
        Doing Tasks
      </div>
      <div className="doing-tasks__bar">
        <div className="doing-tasks__button">
          <input type="checkbox" />
        </div>
        <div className="doing-tasks__name ">{"fjfdhb"}</div>
        <div className="doing-tasks__button">
          <ion-icon name="close"></ion-icon>
        </div>
      </div>
    </div>
  );
}
function CompletedTasks() {
  return (
    <div
      className="doing-tasks"
      style={{
        width: "48%",
      }}
    >
      <div
        className="add-task__title mb-4"
        style={{
          fontSize: "1.6rem",
          textTransform: "capitalize",
        }}
      >
        Add a task
      </div>
      <div className="add-task__bar">
        <div className="add-task__button">
          <ion-icon name="add"></ion-icon>
        </div>
        <div className="add-task__input">
          <input type="text" placeholder="Add task" className=" w-100" />
        </div>
        <div className="add-task__button">
          <ion-icon name="close"></ion-icon>
        </div>
      </div>
    </div>
  );
}
export default function ViewCollection(props) {
  const collection = props.task;

  return (
    <div className="ViewCollection">
      <div
        className="content d-flex flex-column"
        style={{
          textAlign: "left",
          maxWidth: "70rem",
          minWidth: "70rem",
        }}
      >
        <CollectionBar data={collection} />

        <div className="actions__tab d-flex justify-content-between w-100 mt-5">
          <AddTask />
          <SearchTasks />
        </div>

        <div className="tasks__tab d-flex justify-content-between w-100 mt-5">
          <DoingTasks />
          <CompletedTasks />
        </div>
      </div>
    </div>
  );
}
