// lib
import { createUseStyles } from "react-jss";
import clsx from "clsx";
import { useState, useEffect } from "react";
import axios from "axios";
import { toast } from "react-toastify";

import useTaskForm from "../../home/task/actions/services/useTaskForm";

const useStyles = createUseStyles({
  Settings: {
    top: "0",
    left: "0",
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    zIndex: "11",
    color: "#FFFFFF",
  },
  settings__bg: {
    backgroundColor: "#000",
    opacity: "70%",
  },
  settings__container: {
    width: "42rem",
    minWidth: "48rem",
    marginTop: "6rem",
    padding: "2rem",
    backgroundColor: "#1D1D27",
    textAlign: "left",
    borderRadius: "1.6rem",
  },
  settings__caption: {
    fontSize: "2rem",
    textTransform: "capitalize",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  settings__title: {
    fontSize: "1.6rem",
    textTransform: "capitalize",
    marginBottom: "1rem",
  },
  settings__colors: {
    display: "grid",
    gap: "1.5rem",
    gridTemplateColumns: "repeat(auto-fit, 6rem)",
  },
  settings__color: {
    width: "6rem",
    height: "5rem",
    backgroundColor: "transparent",
    cursor: "pointer",
    borderRadius: "1.2rem",
    transition: "all 0.1s ease-in-out",
    position: "relative",
  },
  settings__buttons: {
    gap: "1rem",
    marginTop: "5rem",
  },
  settings__group: {
    marginBottom: "2rem",
    width: "100%",
  },
  settings__err: {
    color: "red",
    padding: "1rem",
    fontSize: "1.4rem",
  },
  settings__listColls: {
    height: "16rem",
    overflowY: "scroll",
    borderRadius: "1.2rem",
    border: "3px solid var(--background-color-header)",
  },
  settings__itemColl: {
    display: "flex",
    alignItems: "center",
    padding: "1rem",
    borderBottom: "2px solid var(--background-color-header)",
    gap: "1rem",
    cursor: "pointer",
  },
  settings__checkbox: {
    color: "var(--bg-btn-pink)",
    backgroundColor: "transparent",
    width: "1.7rem",
    height: "1.7rem",
  },
  settings__lable: {
    fontSize: "1.6rem",
  },
});

const Settings = ({ toggleVal, toggleFunc }) => {
  const classes = useStyles();

  const HandleEditTask = (values) => {
    // axios({
    //   method: "patch",
    //   url: "api/tasks/${task.id}",
    //   data: { title, categoryIds, status },
    // })
    //   .then((res) => {
    //     console.log(res);
    //   })
    //   .catch((err) => {
    //     const errMsg = err.response.data.message;
    //     toast.error(errMsg);
    //   });
  };

  const { handleChange, handleSubmit, values, errors } =
    useTaskForm(HandleEditTask);

  const closeTabFunc = () => {
    toggleFunc();
  };

  return (
    <div
      className={clsx(
        classes.Settings,
        "m-auto position-fixed",
        !toggleVal && "d-none"
      )}
    >
      <div
        className={clsx(classes.settings__bg, "w-100 h-100 position-absolute")}
        onClick={closeTabFunc}
      ></div>

      <div className={clsx(classes.settings__container, "position-absolute")}>
        <form method="post" onSubmit={handleSubmit}>
          <div className="settings__top d-flex w-100">
            <div className={clsx(classes.settings__caption, "mr-auto")}>
              Account Setting
            </div>
            <div className="settings__close-btn" onClick={closeTabFunc}>
              <ion-icon name="close"></ion-icon>
            </div>
          </div>

          <div className={clsx(classes.settings__groups)}>
            <div className={clsx(classes.settings__group, "bar-title")}>
              <div className={clsx(classes.settings__title)}>Old Password</div>
              <input
                type="text"
                placeholder="old password"
                className="btn--outline w-100 py-3"
                style={{ fontSize: "1.6rem" }}
                name="username"
                value={values.title}
                onChange={handleChange}
              />
              {errors.title && (
                <div className={clsx(classes.settings__err)}>
                  *{errors.title}
                </div>
              )}
            </div>
            <div className={clsx(classes.settings__group, "bar-title")}>
              <div className={clsx(classes.settings__title)}>New Password</div>
              <input
                type="text"
                placeholder="new password"
                className="btn--outline w-100 py-3"
                style={{ fontSize: "1.6rem" }}
                name="username"
                value={values.title}
                onChange={handleChange}
              />
              {errors.title && (
                <div className={clsx(classes.settings__err)}>
                  *{errors.title}
                </div>
              )}
            </div>
            <div className={clsx(classes.settings__group, "bar-title")}>
              <div className={clsx(classes.settings__title)}>Re-Password</div>
              <input
                type="text"
                placeholder="re-password"
                className="btn--outline w-100 py-3"
                style={{ fontSize: "1.6rem" }}
                name="username"
                value={values.title}
                onChange={handleChange}
              />
              {errors.title && (
                <div className={clsx(classes.settings__err)}>
                  *{errors.title}
                </div>
              )}
            </div>
          </div>
          
          <div className={clsx(classes.settings__buttons, "w-100 d-flex")}>
            <button
              className="settings__button button btn--none-border btn--hover-bg-gray-light mr-auto mb-0"
              style={{ backgroundColor: "red" }}
              type="button"
            >
              Delete
            </button>
            <button
              className="settings__button button btn--none-border btn--hover-bg-gray-light ml-auto mb-0"
              style={{ backgroundColor: "#3D3C50" }}
              type="submit"
            >
              Submit
            </button>
            <button
              type="reset"
              className="settings__button button btn--none-border btn--hover-bg-gray mb-0"
              onClick={closeTabFunc}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Settings;
