import React from "react";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

const useStyles = createUseStyles({
  taskBar__actions: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "1rem",
  },
});

export default function TaskBar({
  status,
  count,
  handleChangeData,
  handleChangeStatus,
}) {
  const classes = useStyles();

  const handleRemoveTasks = (event) => {};

  return (
    <div
      className="task__title"
      style={{
        fontSize: "2rem",
        textTransform: "capitalize",
        padding: "2rem",
        display: "flex",
        justifyContent: "space-between",
      }}
    >
      <div className="taskBar__details">Total : {count}</div>

      <div className={clsx(classes.taskBar__actions)}>
        <div className="taskBar__action" onClick={handleRemoveTasks}>
          <ion-icon name="trash"></ion-icon>
        </div>
        <div className="taskBar__action">
          <select
            className="form-control-lg"
            aria-label="Default select example"
            onChange={handleChangeOption}
            value={selectedOption}
          >
            {listStatus.map((item) => {
              return (
                <option
                  key={item.value}
                  value={item.value}
                  className="taskBar__option"
                >
                  {item.name}
                </option>
              );
            })}
          </select>
        </div>
      </div>
    </div>
  );
}
