import React from "react";
import { useState, useEffect } from "react";

export default function TaskBar({
  status,
  count,
  handleChangeData,
  handleChangeStatus,
}) {
  const [listStatus, setListStatus] = useState([
    { value: "all", name: "All Task" },
    { value: "inProcess", name: "In Process Task" },
    { value: "complete", name: "Complete Task" },
  ]);
  const [selectedOption, setSelectedOption] = useState();

  function handleChange(event) {
    const optionVal = event.target.value;
    setSelectedOption(event.target.value);
    console.log(event.target.name);
  }

  return (
    <div
      className="task__title"
      style={{
        fontSize: "2rem",
        textTransform: "capitalize",
        padding: "2rem",
        display: "flex",
        justifyContent: "space-between",
      }}
    >
      <div className="taskBar__details">
        {selectedOption} - {count}
      </div>

      <div className="taskBar__actions">
        <div className="taskBar__action">
          <select className="taskBar__select" onChange={handleChange}>
            {listStatus.map((status) => {
              return (
                <option
                  key={status.name}
                  value={status.value}
                  name={status.name}
                  className="taskBar__option"
                >
                  {status.value}
                </option>
              );
            })}
          </select>
        </div>
      </div>
    </div>
  );
}
