// lib
import { createUseStyles } from "react-jss";
import { useState, useEffect } from "react";
import clsx from "clsx";
import axios from "axios";

// Component
import Collection from "./Collection";
import AddCollection from "../actions/addCollection/AddCollection";

import GetCollectionFunc from "../actions/GetCollectionFunc";

const useStyles = createUseStyles({
  title: {
    fontSize: "3rem",
    fontWeight: "bold",
    margin: "2rem 0",
  },
  collections: {
    width: "100%",
    display: "grid",
    gap: "1.7rem",
    transition: "all 0.3s ease",
  },
  actions: {
    gap: "1rem",
  },
  action: {
    padding: "1.2rem 2.4rem !important",
    margin: "auto",
    borderRadius: "1.5rem",
  },
});

export default function Home() {
  const classes = useStyles();
  const [collections, setCollections] = useState([]);
  const [addCollDisplayVal, setAddCollDisplayVal] = useState(false);

  useEffect(async () => {
    const res = await GetCollectionFunc(10, 1);
    const items = res.data.items;
    setCollections(items);
  }, []);
  
  const toggleAddCollDisplayVal = (value) => (event) => {
    setAddCollDisplayVal(value);
  };

  return (
    <div className="Home">
      <ListCollection collections={collections} />
    </div>
  );
}

const ListCollection = ({ collections }) => {


  return (
    <div className="ListCollection">
      
    </div>
  );
};
