import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

const useStyles = createUseStyles({
  addTasks__bar: {
    display: "flex",
    top: "150%",
    right: "0",
    width: "30rem",
    padding: "0 1.3rem",
    borderRadius: "1.3rem",
    border: "3px solid var(--background-color-header)",
    position: "absolute",
    alignItems: "center",
  },
  addTasks__button: {
    padding: "0.3rem",
    borderRadius: "0.7rem",
    display: "flex",
  },
});

export default function AddTask({ handleChange }) {
  const classes = useStyles();

  const [nameTask, setNameTask] = useState("");

  const createTask = () => {};

  return (
    <div className={clsx(classes.addTasks__bar)}>
      <div className={clsx(classes.addTasks__button)} onClick={createTask}>
        <ion-icon
          name="add"
          style={{
            fontSize: "1.7rem",
          }}
        ></ion-icon>
      </div>
      <div className="addTasks__input w-100">
        <input
          type="text"
          placeholder="Add task"
          className="w-100 input--none-border"
          style={{
            fontSize: "1.6rem",
          }}
          
        />
      </div>
      <div className="addTasks__button">
        <ion-icon
          name="close"
          style={{
            fontSize: "2rem",
          }}
        ></ion-icon>
      </div>
    </div>
  );
}
