import React from "react";

export default function Task({ task, handleChangeData }) {
  console.log(task);
  return (
    <div
      className="task__bar w-100 position-relative"
      style={{
        borderRadius: "1.3rem",
        border:
          task.status === "IN_PROGRESS"
            ? `3px solid var(--background-color-header)`
            : "3px solid red",
        padding: "1rem",
      }}
    >
      <div
        className="task__title w-100"
        style={{
          fontSize: "1.6rem",
          padding: "0.5rem 1.5rem",
        }}
      >
        {task.title}
      </div>

      <div
        className="task__btns"
        style={{ border: "1px solid red", display: "flex" , justifyContent: "space-between", alignItems: "center" }}
      >
        <input type="checkbox" className="task__button d-flex"></input>

        <div className="task__button position-relative">
          <ion-icon
            name="ellipsis-vertical"
            style={{
              fontSize: "2rem",
              border: "1px solid red",display: "flex" , justifyContent: "space-between", alignItems: "center"
            }}
          ></ion-icon>
          <div
            className="collection__actions position-absolute d-none"
            style={{
              minWidth: "max-content",
              right: "0",
              borderRadius: "1.1rem",
              backgroundColor: "#1e1e29",
              zIndex: "2",
            }}
          >
            <div
              className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
              style={{
                width: "20rem",
              }}
            >
              edit task
            </div>
            <div
              className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
              style={{
                width: "20rem",
              }}
            >
              delete task
            </div>
          </div>
        </div>
      </div>

      <div
        className="task__process w-100 position-absolute"
        style={{
          top: "100.5%",
          left: "0",
        }}
      >
        <div
          className="task__process-bar"
          style={{
            width: "0%",
            height: "2.5px",
            //   backgroundColor: `${collection.cColor}`,
          }}
        ></div>
      </div>
    </div>
  );
}
