import React from "react";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

import AddTask from "../actions/addTask/AddTask";
import SearchTasks from "../actions/searchTasks/SearchTasks";

const useStyles = createUseStyles({
  taskBar__container: {
    textTransform: "capitalize",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    marginBottom: "2rem",
    width: "80%",
    maxWidth: "100rem",
  },
  taskBar__actions: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "1rem",
  },
  taskBar__action: {
    height: "100%",
    padding: "0.8rem 2rem !important",
    marginBottom: "0",
    borderRadius: "1.5rem",
    position: "relative",
  },
  taskBar__input: {
    minWidth: "5rem",
    padding: "1rem",
    width: "3rem !important",
    fontSize: "1.5rem",
  },
  taskBar__detail: {
    fontSize: "2rem",
    fontWeight: "700",
  },
  taskBar__lable: {
    fontSize: "1.5rem",
  },
});

const useActions = () => {
  const [displayAddBarVal, setDisplayAddBarVal] = useState(false);
  const [displaySearchBarVal, setDisplaySearchBarVal] = useState(false);

  // start - collection
  const toggleEditCollDisplayVal = (value) => (event) => {
    setEditCollDisplayVal(value);
  };
  const toggleDeleteCollDisplayVal = (value) => (event) => {
    setDeleteCollDisplayVal(value);
  };

  const toggleAddBar = () => {
    setDisplayAddBarVal(!displayAddBarVal);
    setDisplaySearchBarVal(false);
    setDisplayCollectionActionsBarVal(false);
  };
  const toggleSearchBar = () => {
    setDisplaySearchBarVal(!displaySearchBarVal);
    setDisplayAddBarVal(false);
    setDisplayCollectionActionsBarVal(false);
  };
  const toggleCollectionActionsBar = () => {
    setDisplayCollectionActionsBarVal(!displayCollectionActionsBarVal);
    setDisplayAddBarVal(false);
    setDisplaySearchBarVal(false);
  };

  return {
    toggleEditCollDisplayVal,
    toggleDeleteCollDisplayVal,
    editCollDisplayVal,
    deleteCollDisplayVal,
    toggleAddBar,
    toggleSearchBar,
    toggleCollectionActionsBar,
    displayCollectionActionsBarVal,
    displayAddBarVal,
    displaySearchBarVal,
  };
};

export default function TaskBar({ listCollections, count, handleChangeData }) {
  const classes = useStyles();

  const {
    toggleAddBar,
    toggleSearchBar,
    displayAddBarVal,
    displaySearchBarVal,
  } = useActions();

  const handleRemoveTasks = (event) => {};

  return (
    <div className={clsx(classes.taskBar__container)}>
      <div className={clsx(classes.taskBar__details)}>
        <div className={clsx(classes.taskBar__detail)}>Total : {count}</div>
      </div>

      <div
        className={clsx(classes.taskBar__actions, "d-flex align-items-center")}
      >
        <button
          className={clsx(
            classes.taskBar__action,
            "button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
          )}
        >
          <div className="taskAction__icon" onClick={toggleAddBar}>
            <ion-icon name="add"></ion-icon>
          </div>
        </button>

        <button
          className={clsx(
            classes.taskBar__action,
            "button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
          )}
        >
          <div className="taskAction__icon" onClick={toggleSearchBar}>
            <ion-icon name="search"></ion-icon>
          </div>
        </button>

        <button
          className={clsx(
            classes.taskBar__action,
            "button btn--flex btn--bg-gray btn--border btn--hover-bg-gray"
          )}
          onClick={handleRemoveTasks}
        >
          <ion-icon name="trash"></ion-icon>
        </button>
      </div>

      <AddTask
        displayVal={displayAddBarVal}
        toggleFunc={toggleAddBar}
        listCollections={listCollections}
        handleChangeData={handleChangeData}
        />
        <SearchTasks
        displayVal={displaySearchBarVal}
        toggleFunc={toggleSearchBar}
        listCollections={listCollections}
        handleChangeData={handleChangeData}
      />
    </div>
  );
}
