// lib
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

// Component
import CollectionBar from "./CollectionBar";

function DoingTasks({collection}) {
  return (
    <div
      className="doing-tasks__tab"
      style={{
        width: "48%",
      }}
    >
      <div
        className="doing-tasks__title mb-4"
        style={{
          fontSize: "1.6rem",
          textTransform: "capitalize",
        }}
      >
        Doing Tasks - {0}
      </div>
      <div
        className="doing-tasks__bar w-100 position-relative d-flex align-items-center"
        style={{
          padding: "0.3rem 1.3rem 1.3rem 1.3rem",
          borderTopLeftRadius: "1.3rem",
          borderTopRightRadius: "1.3rem",
          border: "3px solid var(--background-color-header)",
        }}
      >
        <input
          type="checkbox"
          className="doing-tasks__button d-flex"
          style={{
            padding: "0.3rem",
            borderRadius: "0.7rem",
            backgroundColor: `${collection.cColor}`,
          }}
        ></input>

        <div
          className="doing-tasks__input w-100"
          style={{
            fontSize: "1.6rem",
            padding: "0.5rem 1.5rem",
          }}
        >
          {
            "sjdbnsdjv jdfkghfjb n euig hergigheruigheruig er g ueru g er ergu erigheru ierh gaserui ghergui "
          }
        </div>

        <div
          className="doing-tasks__button position-relative"
          style={{
            backgroundColor: "var()",
          }}
        >
          <ion-icon
            name="ellipsis-vertical"
            style={{
              fontSize: "2rem",
              cursor: "pointer",
            }}
          ></ion-icon>
          <div
            className="collection__actions position-absolute"
            style={{
              minWidth: "max-content",
              right: "0",
              borderRadius: "1.1rem",
              backgroundColor: "#1e1e29",
              zIndex: "2",
            }}
          >
            <div
              className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
              style={{
                width: "20rem",
              }}
            >
              edit task
            </div>
            <div
              className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
              style={{
                width: "20rem",
              }}
            >
              delete task
            </div>
          </div>
        </div>

        <div
          className="doing-tasks__process w-100 position-absolute"
          style={{
            top: "100.5%",
            left: "0",
          }}
        >
          <div
            className="doing-tasks__process-bar"
            style={{
              width: "0%",
              height: "2.5px",
              backgroundColor: `${collection.cColor}`,
            }}
          ></div>
        </div>
      </div>
    </div>
  );
}
function CompletedTasks(props) {
  const collection = props.data;
  return (
    <div
      className="doing-tasks__tab"
      style={{
        width: "48%",
      }}
    >
      <div
        className="doing-tasks__title mb-4"
        style={{
          fontSize: "1.6rem",
          textTransform: "capitalize",
        }}
      >
        Completed Tasks - {0}
      </div>
      <div
        className="doing-tasks__bar position-relative w-100 d-flex align-items-center"
        style={{
          padding: "0.3rem 1.3rem 1.3rem 1.3rem",
          borderTopLeftRadius: "1.3rem",
          borderTopRightRadius: "1.3rem",
          border: "3px solid var(--background-color-header)",
        }}
      >
        <div
          className="doing-tasks__button d-flex"
          style={{
            padding: "0.3rem",
            borderRadius: "0.7rem",
            backgroundColor: `${collection.cColor}`,
          }}
        >
          <ion-icon
            name="checkmark"
            style={{
              fontSize: "1.7rem",
              cursor: "pointer",
            }}
          ></ion-icon>
        </div>

        <div
          className="doing-tasks__input w-100"
          style={{
            fontSize: "1.6rem",
            padding: "0.5rem 1.5rem",
          }}
        >
          {
            "sjdbnsdjv jdfkghfjb n euig hergigheruigheruig er g ueru g er ergu erigheru ierh gaserui ghergui "
          }
        </div>

        <div
          className="doing-tasks__button"
          style={{
            backgroundColor: "var()",
          }}
        >
          <ion-icon
            name="ellipsis-vertical"
            style={{
              fontSize: "2rem",
              cursor: "pointer",
            }}
          ></ion-icon>
          <div
            className="collection__actions position-absolute"
            style={{
              minWidth: "max-content",
              right: "0",
              borderRadius: "1.1rem",
              backgroundColor: "#1e1e29",
              zIndex: "2",
            }}
          >
            <div
              className="collection__action button btn--none-border btn--full-width btn--none-radius btn--hover-bg-gray m-0 text-left text-capitalize"
              style={{
                width: "20rem",
              }}
            >
              delete task
            </div>
          </div>
        </div>

        <div
          className="doing-tasks__process w-100 position-absolute"
          style={{
            top: "100.5%",
            left: "0",
          }}
        >
          <div
            className="doing-tasks__process-bar"
            style={{
              width: "100%",
              height: "2.5px",
              backgroundColor: `${collection.cColor}`,
            }}
          ></div>
        </div>
      </div>
    </div>
  );
}



export default function ViewCollection() {
  let { cId } = useParams();

  const [collection, setCollection] = useState({});

  useEffect(() => {
    if (cId) {
      axios
        .get(`api/categories/${cId}`)
        .then((res) => {
          setCollection(res.data);
        })
        .catch((err) => {
          const errMsg = err.response.data.message;
          console.log(errMsg);
        });
    }
  }, []);

  const handleChange = (data) => {
    setCollection(data);

    
  };

  return (
    <div className="ViewCollection">
      <div
        className="content d-flex flex-column"
        style={{
          textAlign: "left",
          maxWidth: "70rem",
          minWidth: "70rem",
        }}
      >
        <div className="tasks__tab d-flex justify-content-between w-100 mt-5">
          <CollectionBar collection={collection} handleChange={handleChange} />
        </div>
      </div>
    </div>
  );
}
