// lib
import { createUseStyles } from "react-jss";
import { Routes, Route, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import clsx from "clsx";

// components

// =================================================================
const useStyles = createUseStyles({
  Header: {
    padding: "2rem",
    fontSize: "1.6rem",
    backgroundColor: " #1d1d26",
    zIndex: "9",
    width: "15rem"
  },
  header__logo: {
    textTransform: "uppercase",
    fontSize: "3rem",
    fontWeight: "700",
    gap: "1rem",
    color: "var(--bg-btn-pink)",
  },
});

export default function SideBar() {
  const classes = useStyles();

  return (
    <div
      className={`${classes.Header} position-fixed h-100 t-0 d-flex flex-column align-items-center justify-content-center `}
    >
      <Link
        to=""
        className={`${classes.header__logo} d-flex align-items-center`}
      >
        <ion-icon name="dice"></ion-icon>
        <div className={classes.header__logo_text}>todo app</div>
      </Link>
      <Link
        to=""
        className={`${classes.header__logo} d-flex align-items-center`}
      >
        <ion-icon name="dice"></ion-icon>
        <div className={classes.header__logo_text}>todo app</div>
      </Link>
    </div>
  );
}
