// lib
import React from "react";
import { createUseStyles } from "react-jss";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

// Component
import Header from "./components/header/Header";
import Routers from "./routers/Routers";

import { ToastContainer, toast, Slide, Flip, Bounce } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
// Service

// Css
import "./App.css";

// =================================================================
const useStyles = createUseStyles({
  App: {
    backgroundImage: "url(https://tsks.app/img/signin_balls.png)",
    backgroundColor: "#181820",
    backgroundPosition: "top",
    backgroundSize: "contain",
    backgroundRepeat: "no-repeat",
  },
});

function App() {
  const navigate = useNavigate();
  const classes = useStyles();

  const [user, setUser] = useState({ username: "", id: "" });

  // Function
  const HandleLogin = (details) => {
    setUser({
      username: details.username,
      id: details.id,
    });
    toast.success("ðŸ¦„ Loggin successfully!");

    // Redirect
    navigate("collections");
  };
  const HandleLogout = () => {
    setUser({
      username: "",
      id: "",
    });

    localStorage.removeItem("auth_token");
    localStorage.removeItem("auth_id");

    toast.success("ðŸ¦„ Logout successfully!");

    // Redirect
    navigate("");
  };

  useEffect(() => {
    let auth_id = localStorage.getItem("auth_id");
    if (auth_id) {
      axios
        .get(`api/users/${auth_id}`)
        .then((res) => {
          setUser({
            username: res.data.username,
            id: res.data.id,
          });
        })
        .catch((err) => {
          const errMsg = err.response.data.message;
          console.log(errMsg);
        });
    }
  }, []);

  return (
    <div className={classes.App}>
      <Header HandleLogout={HandleLogout} user={user} />
      <ToastContainer
        position="top-center"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        transition={Bounce}
        style={{ fontSize: "1.4rem" }}
      />

      <Routers HandleLogin={HandleLogin} user={user} />
    </div>
  );
}
export default App;
