// lib
import { createUseStyles } from "react-jss";
import { useState, useEffect } from "react";
import clsx from "clsx";

const useStyles = createUseStyles({
  AddCollection: {
    top: "0",
    left: "0",
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    // alignItems: "center",
  },
  addCollection__bg: {
    backgroundColor: "#000",
    opacity: "50%",
  },
  addCollection__container: {
    width: "42rem",
    minWidth: "42rem",
    padding: "2rem",
    marginTop: "8.5rem",
    backgroundColor: "#1D1D27",
    textAlign: "left",
    borderRadius: "1.6rem",
  },
  addCollection__caption: {
    fontSize: "2rem",
    textTransform: "capitalize",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  addCollection__title: {
    fontSize: "1.6rem",
    textTransform: "capitalize",
    marginBottom: "1rem",
  },
  addCollection__colors: {
    display: "grid",
    gap: "1.5rem",
    gridTemplateColumns: "repeat(auto-fit, 6rem)",
  },
  addCollection__color: {
    width: "6rem",
    height: "5rem",
    backgroundColor: "transparent",
    cursor: "pointer",
    borderRadius: "1.2rem",
    transition: "all 1s ease",
  },
  addCollection__buttons: {
    gap: "1rem",
    marginTop: "5rem",
  },
  addCollection__bar:{
    
  }
});

export default function AddCollection(props) {
  const classes = useStyles();
  let listColors = ["pink", "yellow", "orange", "red", "purple", "blue"];
  let displayVal = props.displayVal;

  return (
    <div className={clsx(classes.AddCollection, "m-auto position-fixed")}>
      <div
        className={clsx(
          classes.addCollection__bg,
          "w-100 h-100 position-absolute"
        )}
      ></div>

      <div
        className={clsx(classes.addCollection__container, "position-absolute")}
      >
        <div className="addCollection__top d-flex w-100">
          <div className={clsx(classes.addCollection__caption, "mr-auto")}>
            add collection
          </div>
          <div className="addCollection__close-btn">
            <ion-icon
              name="close"
              style={{ fontSize: "2.5rem", cursor: "pointer" }}
            ></ion-icon>
          </div>
        </div>

        <div
          className="addCollection__bar bar-name w-100"
          style={{ marginBottom: "2rem" }}
        >
          <div className={clsx(classes.addCollection__title)}>name</div>
          <input
            type="text"
            placeholder="My Collection"
            className="btn--outline w-100 py-3"
            style={{ fontSize: "1.6rem" }}
          />
        </div>

        <div
          className="addCollection__bar bar-color w-100"
          style={{ marginBottom: "2rem" }}
        >
          <div className={clsx(classes.addCollection__title)}>color</div>
          <div className={clsx(classes.addCollection__colors, "w-100")}>
            {listColors.map(function (color) {
              return (
                <div
                  className={clsx(classes.addCollection__color)}
                  style={{
                    border: `3px solid ${color}`,
                  }}
                ></div>
              );
            })}
          </div>
        </div>

        <div
          className="addCollection__bar bar-custom-color w-100"
          style={{ marginBottom: "2rem" }}
        >
          <div className={clsx(classes.addCollection__title)}>
            custome color
          </div>
          <div
            className={clsx(classes.addCollection__color)}
            style={{
              backgroundColor: "#FC76A1",
            }}
          ></div>
        </div>

        <div className={clsx(classes.addCollection__buttons, "w-100 d-flex")}>
          <button
            className="addCollection__button button btn--none-border btn--hover-bg-gray-light ml-auto mb-0"
            style={{ backgroundColor: "#3D3C50" }}
          >
            Create
          </button>
          <button className="addCollection__button button btn--none-border btn--hover-bg-gray mb-0">
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
}
