// lib
import { createUseStyles } from "react-jss";
import { useState, useEffect } from "react";
import clsx from "clsx";

const useStyles = createUseStyles({
  AddCollection: {
    top: "0",
    left: "0",
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    // alignItems: "center",
  },
  addCollection__bg: {
    backgroundColor: "#000",
    opacity: "50%",
  },
  addCollection__container: {
    width: "42rem",
    minWidth: "42rem",
    padding: "2rem",
    marginTop: "8.5rem",
    backgroundColor: "#1D1D27",
    textAlign: "left",
    borderRadius: "1.6rem",
  },
  addCollection__caption: {
    fontSize: "2rem",
    textTransform: "capitalize",
    fontWeight: "bold",
    marginBottom: "2rem",
  },
  addCollection__title: {
    fontSize: "1.6rem",
    textTransform: "capitalize",
    marginBottom: "1rem",
  },
  addCollection__colors: {
    display: "grid",
    gap: "1.5rem",
    gridTemplateColumns: "repeat(auto-fit, 6rem)",
  },
  addCollection__color: {
    height: "5rem",
    backgroundColor: "transparent",
    cursor: "pointer",
    borderRadius: "1.2rem",
    transition: "all 1s ease",
  },
  addCollection__buttons: {
    gap: "1rem",
    marginTop: "5rem",
  },
});

export default function AddCollection(props) {
  let listColors = ["pink", "yellow", "orange", "red", "purple", "blue"];

  let displayVal = props.displayVal;
  return (
    <div
      className="AddCollection m-auto position-fixed d-none"
      style={{
        display: `${displayVal ? "block" : "none"}`,
      }}
    >
      <div className="addCollection__bg w-100 h-100 position-absolute"></div>

      <div className="addCollection__container position-absolute">
        <div className="addCollection__top d-flex w-100">
          <div className="addCollection__caption mr-auto" style={{}}>
            add collection
          </div>
          <div className="addCollection__close-btn">
            <ion-icon
              name="close"
              style={{ fontSize: "2.5rem", cursor: "pointer" }}
            ></ion-icon>
          </div>
        </div>

        <div
          className="addCollection__bar bar-name w-100"
          style={{ marginBottom: "2rem" }}
        >
          <div className="addCollection__title" style={{}}>
            name
          </div>
          <input
            type="text"
            placeholder="My Collection"
            className="btn--outline w-100 py-3"
            style={{ fontSize: "1.6rem" }}
          />
        </div>

        <div
          className="addCollection__bar bar-color w-100"
          style={{ marginBottom: "2rem" }}
        >
          <div className="addCollection__title">color</div>
          <div className="addCollection__colors w-100">
            {listColors.map(function (color) {
              return <div className="addCollection__color"></div>;
            })}
          </div>
        </div>

        <div
          className="addCollection__bar bar-custom-color w-100"
          style={{ marginBottom: "2rem" }}
        >
          <div className="addCollection__title">custome color</div>
          <div
            className="addCollection__color"
            style={{
              width: "6rem",
              height: "5rem",
              backgroundColor: "#FC76A1",
              borderRadius: "1.2rem",
              cursor: "pointer",
            }}
          ></div>
        </div>

        <div className="addCollection__buttons w-100 d-flex ">
          <button
            className="addCollection__button button btn--none-border btn--hover-bg-gray-light ml-auto mb-0"
            style={{ backgroundColor: "#3D3C50" }}
          >
            Create
          </button>
          <button className="addCollection__button button btn--none-border btn--hover-bg-gray mb-0">
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
}
