import React from "react";
import { useState, useEffect } from "react";
import { createUseStyles } from "react-jss";
import clsx from "clsx";

const useStyles = createUseStyles({
  taskBar__actions: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "1rem",
  },
  taskBar__input: {
    padding: "0",
    height: "100%",
    width: "3rem !important",
    fontSize: "1rem",
  },
});

export default function TaskBar({
  status,
  count,
  handleChangeData,
  handleChangeStatus,
}) {
  const classes = useStyles();

  const handleRemoveTasks = (event) => {};

  return (
    <div
      className={clsx(classes.task__title)}
      style={{
        fontSize: "2rem",
        textTransform: "capitalize",
        padding: "2rem",
        display: "flex",
        justifyContent: "space-between",
      }}
    >
      <div className={clsx()}>Total : {count}</div>

      <div className={clsx(classes.taskBar__actions)}>
        <div className={clsx(classes.taskBar__action)}>
          <span className={clsx(classes.taskBar__lable)}>Limit</span>
          <input type="number" className={clsx(classes.taskBar__input)} />
        </div>

        <div
          className={clsx(classes.taskBar__action)}
          onClick={handleRemoveTasks}
        >
          <ion-icon name="trash"></ion-icon>
        </div>
      </div>
    </div>
  );
}
