import React from "react";

function CollectionBar(props) {
  const collection = props.data;

  return (
    <div className="collection__bar d-flex align-items-center w-100" style={{}}>
      <div
        className="collection__back-btn"
        style={{
          padding: "1.2rem",
          marginRight: "1.5rem",
          borderRadius: "1.5rem",
          backgroundColor: "var(--background-color-header)",
        }}
      >
        <ion-icon
          name="chevron-back-outline"
          style={{
            fontSize: "2.7rem",
          }}
        ></ion-icon>
      </div>
      <div
        className="collection__name"
        style={{
          fontSize: "3rem",
          textTransform: "capitalize",
        }}
      >
        {collection.cName}
      </div>
      <div className="collection__dots ml-auto position-relative">
        <ion-icon
          name="ellipsis-horizontal"
          style={{
            fontSize: "2.5rem",
          }}
        ></ion-icon>
        <div className="collection__actions position-absolute d-none">
          <div className="collection__action">edit collection</div>
          <div className="collection__action">delete collection</div>
        </div>
      </div>
    </div>
  );
}
function AddTask(props) {
  const collection = props.data;
  return (
    <div
      className="add-task__tab"
      style={{
        width: "48%",
      }}
    >
      <div
        className="add-task__title mb-4"
        style={{
          fontSize: "1.6rem",
          textTransform: "capitalize",
        }}
      >
        Add a task
      </div>
      <div
        className="add-task__bar w-100 d-flex align-items-center"
        style={{
          padding: "0.3rem 1.3rem",
          borderRadius: "1.3rem",
          border: "3px solid var(--background-color-header)",
        }}
      >
        <div
          className="add-task__button d-flex"
          style={{
            padding: "0.3rem",
            borderRadius: "0.7rem",
            backgroundColor: `${collection.cColor}`,
          }}
        >
          <ion-icon
            name="add"
            style={{
              fontSize: "1.7rem",
              cursor: "pointer",
            }}
          ></ion-icon>
        </div>
        <div className="add-task__input w-100">
          <input
            type="text"
            placeholder="Add task"
            className="w-100 input--none-border"
            style={{
              fontSize: "1.6rem",
            }}
          />
        </div>
        <div
          className="add-task__button"
          style={{
            backgroundColor: "var()",
          }}
        >
          <ion-icon
            name="close"
            style={{
              fontSize: "2rem",
              cursor: "pointer",
            }}
          ></ion-icon>
        </div>
      </div>
    </div>
  );
}
