// lib
import React from "react";
import { createUseStyles } from "react-jss";
import { Routes, Route } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

// Component
import Home from "./components/home/Home";
import ScreenSaver from "./components/screenSaver/ScreenSaver";
import Users from "./components/users/Users";
import Header from "./components/header/Header";

import Routers from "./routers/Routers";

// Css
import "./App.css";

// =================================================================
const useStyles = createUseStyles({
  App: {
    backgroundImage: "url(" + "https://tsks.app/img/signin_balls.png" + ")",
    backgroundColor: "#181820",
    backgroundPosition: "top",
    backgroundSize: "contain",
    backgroundRepeat: "no-repeat",
  },
});

function App() {
  const classes = useStyles();
  const [user, setUser] = useState({ username: "", id: "" });
  const [error, setError] = useState("");

  // Function
  const Register = async ({ username, password, ...args }) => {
    // let res = await axios({
    //   method: "post",
    //   url: "https://mvn-task-manager.work/auth/register",
    //   data: { username, password },
    // });

    // console.log(res.data);
    // if (res && res.data) {
    //   console.log("Logged in");
    // } else {
    //   console.log("Username already exists!");
    //   setError("Username already exists!");
    // }

    const myPromise = new Promise((resolve, reject) => {
      let res = axios({
        method: "post",
        url: "https://mvn-task-manager.work/auth/register",
        data: { username, password },
      });

      resolve(res);
    });

    myPromise
      .then((result) => {
        console.log(result);
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
        setError(errMsg);
      });
  };
  const Login = async ({ username, password, ...args }) => {
    if (res && res.data) {
      console.log(res.data);

      
    } else {
      console.log("Details not match!");
      setError("Username or password incorrect");
    }

    const myPromise = new Promise((resolve, reject) => {
      let res = axios({
        method: "post",
        url: "https://mvn-task-manager.work/auth/login",
        data: { username, password },
      });

      resolve(res);
    });

    myPromise
      .then((result) => {
        setUser({
          username: username,
          id: result.data.id,
        });
        console.log("Logged in");
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
        setError(errMsg);
      });
  };
  const Logout = () => {
    setUser({
      username: "",
      password: "",
    });
    console.log(user);
  };
  // End-Function

  return (
    <div className={classes.App}>
      <Header user={user} Logout={Logout} />

      <Routes>
        {user.username !== "" ? (
          <Route exact path="" element={<Home />} />
        ) : (
          <Route
            exact
            path=""
            element={<ScreenSaver user={user} Login={Login} />}
          />
        )}
      </Routes>

      <Routers Login={Login} Register={Register} error={error} />
    </div>
  );
}
export default App;
