// lib
import React from "react";
import { createUseStyles } from "react-jss";
import { Routes, Route } from "react-router-dom";
import { withRoutes } from "react-router";
import { useState, useEffect } from "react";
import axios from "axios";

// Component
import Home from "./components/home/Home";
import ScreenSaver from "./components/screenSaver/ScreenSaver";
import Users from "./components/users/Users";
import Header from "./components/header/Header";
import Routers from "./routers/Routers";

import IsEmptyObject from "./components/IsEmptyObject";

// Css
import "./App.css";

// =================================================================
const useStyles = createUseStyles({
  App: {
    backgroundImage: "url(" + "https://tsks.app/img/signin_balls.png" + ")",
    backgroundColor: "#181820",
    backgroundPosition: "top",
    backgroundSize: "contain",
    backgroundRepeat: "no-repeat",
  },
});

function App() {
  const classes = useStyles();
  const [user, setUser] = useState({ username: "", id: "" });

  // Function
  const HandleLogin = (details) => {
    setUser({
      username: details.username,
      id: details.id,
    });

    console.log("Logged in successfully");

    // Redirect    
  };
  const Logout = () => {
    setUser({
      username: "",
      id: "",
    });
    console.log(user);
  };

  useEffect(() => {
    const config = {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("auth_token"),
      },
    };

    axios
      .get(`api/users/${localStorage.getItem("auth_id")}`, config)
      .then((res) => {
        console.log(res);
        setUser({
          username: res.data.username,
          id: res.data.id,
        });
      })
      .catch((err) => {
        const errMsg = err.response.data.message;
        console.log(errMsg);
      });
  }, []);

  return (
    <div className={classes.App}>
      <Header user={user} Logout={Logout} />

      <Routers HandleLogin={HandleLogin} user={user} />
    </div>
  );
}
export default withRoutes(App);
